mapper.select:
select trim(endccd) as endccd ,trim(endcnm) as endcnm ,regu ,regd ,modu ,modd from PSZDBLIB.SPZ100PF where 'SqlMap-FinlCustInfoBase.searchByCondition' = 'SqlMap-FinlCustInfoBase.searchByCondition'
AND endccd = #{endccd}
AND endcnm = #{endcnm}
order by endccd

mapper.insert:
insert into PSZDBLIB.SPZ100PF(
	/* 'SqlMap-FinlCustInfoBase.create' = 'SqlMap-FinlCustInfoBase.create' */
	endccd,endcnm,regu,regd
) values(
	 (SELECT 'M' || REPEAT('0', 3-LENGTH(TRIM(CHAR(NEXTVAL FOR PSZDBLIB.SAL_FINLCUSTTYPE_SEQ)))) || CHAR(NEXTVAL FOR PSZDBLIB.SAL_FINLCUSTTYPE_SEQ) AS AA 
	 FROM SYSIBM.SYSDUMMY1)
	,#{endcnm}
	,#{regu}
	,REPLACE(CHAR(DATE(CURRENT DATE), ISO), '-','')

)

mapper.update:

update PSZDBLIB.SPZ100PF
set
	endcnm = #{endcnm}
	,modu = #{modu}
	,modd = REPLACE(CHAR(DATE(CURRENT DATE), ISO), '-','')
where
	'SqlMap-FinlCustInfoBase.update' = 'SqlMap-FinlCustInfoBase.update'
AND endccd = #{endccd}
