mapper.sql:
    SELECT
    	A.COMPS, A.TYPCD, A.PYVND, TRIM(B.CUSNM) AS CUSNM, A.FACCD, A.YARD, 
		A.SHPTO, D.CPRTN AS MKPNO, A.PSPNO, TRIM(C.ITDSC) AS ITDSC, A.OUTQY

			   ,'${item}' AS PLNDT${index} 


			   , A.BASQY${index}

    FROM (
	     SELECT
			A.COMPS, A.TYPCD, A.PYVND, A.FACCD, A.YARD, 
			A.SHPTO,A.PSPNO, A.OUTQY

			,SUM(CASE WHEN A.PLNDT = '${item}' THEN A.BASQY END) AS BASQY${index}

		FROM 

			PSZDBLIB.SVZ611PF A


			PSZDBLIB.SVZ601PF A


			PSZDBLIB.SVZ612PF A


			PSZDBLIB.SVZ602PF A

		WHERE
			A.COMPS = #{comps}
			AND A.PYVND = #{pyvnd}
			AND A.CHGDT= ${plndt}
			AND A.PLNDT BETWEEN ${stPlndt} AND ${enPlndt}
		GROUP BY A.COMPS, A.TYPCD, A.PYVND, A.FACCD, A.YARD, A.SHPTO, A.PSPNO, A.OUTQY
	) A
	LEFT OUTER JOIN ${lib1}.CUSMAS B ON A.PYVND=B.CUSNO	
	LEFT OUTER JOIN ${lib1}.BM008PR C ON A.PSPNO=C.ITNBR
	LEFT OUTER JOIN (
		SELECT X.CUSNO, X.CPRTN, X.PRTNO, X.EDATM	
		FROM ${lib1}.EM000PR X 
		WHERE X.EDATM = (SELECT MAX(Y.EDATM) FROM ${lib1}.EM000PR Y WHERE X.CUSNO=Y.CUSNO AND X.PRTNO=Y.PRTNO)
		) D ON A.PYVND=D.CUSNO AND A.PSPNO=D.PRTNO

mapper.select:
SELECT A.PYVND, A.CUSNM, A.FACCD, A.YARD, A.SHPTO, A.MKPNO, A.PSPNO, A.ITDSC, A.OUTQY

	   , A.PLNDT${index}


	   ,A.BASQY${index}

FROM (

	) A
WHERE 
      'SqlMap-NonOrderHostUpList.searchByCondition' = 'SqlMap-NonOrderHostUpList.searchByCondition'
ORDER BY A.PYVND, A.SHPTO, A.PSPNO

SELECT A.PYVND, A.CUSNM, A.FACCD, A.YARD, A.SHPTO, A.MKPNO, A.PSPNO, A.ITDSC, A.OUTQY, 0 AS LACKCNT 

	   , A.PLNDT${index}


	   ,A.BASQY${index}


	   ,CAST(stock${index}.STOCK${index} AS INT) AS stock${index}


FROM (

	) A



		LEFT OUTER JOIN (
			SELECT Z.COMPS, Z.ITNBR, SUM(Z.MOHTQ) AS STOCK${index} FROM ${lib2}.BDD100PF Z
			WHERE
			Z.COMPS = #{comps} 
			AND HOUSE = #{item}
			GROUP BY Z.COMPS, Z.ITNBR
		) stock${index} ON A.COMPS = stock${index}.COMPS AND A.PSPNO = stock${index}.ITNBR



WHERE 
      'SqlMap-NonOrderHostUpList.searchByCondition2' = 'SqlMap-NonOrderHostUpList.searchByCondition2'
ORDER BY A.PYVND, A.SHPTO, A.PSPNO
