mapper.select:
SELECT COMPS , REPYM , REPRTSE , CMNITM , TRIM(CMNCN) AS CMNCN , TRIM(COMPS) AS OLD_COMPS , TRIM(REPYM) AS OLD_REPYM , TRIM(REPRTSE) AS OLD_REPRTSE , TRIM(CMNITM) AS OLD_CMNITM , TRIM(CMNCN) AS OLD_CMNCN FROM PSZDBLIB.SPZ130PF WHERE 'SqlMap-SalAnlysComent.searchByCondition' = 'SqlMap-SalAnlysComent.searchByCondition'
AND COMPS = #{comps}
AND REPYM = #{repym}
AND REPRTSE = #{reprtse}
ORDER BY REPYM

   SELECT 
         COUNT(*) CNT 
   FROM 
        PSZDBLIB.SPZ130PF
   WHERE
   'SqlMap-SalAnlysComent.searchCount' = 'SqlMap-SalAnlysComent.searchCount'
   AND COMPS = #{comps}
	AND   REPYM = #{repym}
	AND   REPRTSE = #{reprtse}
	AND   CMNITM = #{cmnitm}

mapper.insert:
    INSERT INTO PSZDBLIB.SPZ130PF (
