		, CMNITM
		, CMNCN
		, REGU
		, REGD
    ) values (
		#{comps}
       ,#{repym}
       ,#{reprtse}
       ,#{cmnitm}
       ,#{cmncn}
       ,#{regu}
       ,REPLACE(CHAR(DATE(CURRENT DATE), ISO), '-','')
    )

mapper.update:
   UPDATE
    	PSZDBLIB.SPZ130PF  
    SET
		COMPS = #{comps}
		, REPYM = #{repym}
		, REPRTSE = #{reprtse}
		, CMNITM = #{cmnitm}
		, CMNCN = #{cmncn}
		, MODU = #{modu}
        , MODD = REPLACE(CHAR(DATE(CURRENT DATE), ISO), '-','')
    WHERE
    'SqlMap-SalAnlysComent.update' = 'SqlMap-SalAnlysComent.update'
    AND COMPS = #{oldComps}
	AND REPYM = #{oldRepym}
	AND REPRTSE = #{oldReprtse}
	AND CMNITM = #{oldCmnitm}

