mapper.insert:
    INSERT INTO TB_ED_EDUCATION_INFO(
			 ID
			,RGSTR_ID
			,RGST_DT
			,UPDTR_ID
			,UPDT_DT
			,EDU_DIV
			,EDU_NM
			,EDU_DT
			,RECEIPT_END_DATE
			,PERS_CNT
			,ONOFF_YN
			,USE_YN
			,REMARK
	)VALUES(
			 #{id}
			,#{rgstrId}
			,#{rgstDt}
			,#{updtrId}
			,#{updtDt}
			,#{eduDiv}
			,#{eduNm}
			,TO_DATE(#{eduDt}, 'YYYY-MM-DD HH24:MI')
			,TO_DATE(#{receiptEndDate}, 'YYYY-MM-DD HH24:MI')
			,#{persCnt}
			,#{onoffYn}
			,#{useYn}
			,#{remark}
	)


      SELECT SEQ_TB_ED_EDUCATION_INFO.NEXTVAL FROM DUAL

mapper.update:
    UPDATE TB_ED_EDUCATION_INFO
     SET UPDTR_ID = #{updtrId}
		,UPDT_DT = #{updtDt}
		,EDU_DIV = #{eduDiv}
		,EDU_NM = #{eduNm}
		,EDU_DT = TO_DATE(#{eduDt}, 'YYYY-MM-DD HH24:MI')
		,RECEIPT_END_DATE = TO_DATE(#{receiptEndDate}, 'YYYY-MM-DD HH24:MI')
		,PERS_CNT = #{persCnt}
		,ONOFF_YN = #{onoffYn}
		,USE_YN = #{useYn}
		,REMARK = #{remark}

 WHERE ID = #{id}
