mapper.select:
SELECT *
FROM (
     SELECT ROWNUM AS RN, A.*
      FROM (




      ) A
      ORDER BY A.ID
 ) AA


  WHERE RN BETWEEN ((${pageable.pageNumber} - 1) * ${pageable.pageSize}) + 1 AND ${pageable.offset}

ORDER BY RN

SELECT COUNT(ID) AS CNT
FROM (




 ) AA

mapper.insert:
    INSERT INTO TB_DC_CONSULTING (
		ID
		,VINNO
		,MANAGE_NO
		,SERVICE_DEGREE
		,CONSULTING_DEGREE
		,CONSULTING_NM
		,CONSULTING_TYPE
		,CHECK_CONT
		,CONSULTING_CONT
		,CONSULTING_RESULT
		,AUTOINSERT
		,CONSULTING_DATE
		,AUDIT_INFO
		,SD_APPOINT
		,RGSTR_ID
		,RGST_DT
		,UPDTR_ID
		,UPDT_DT

    ) VALUES (
		#{id}
		,UPPER(#{vinno})
		,#{manageNo}
		,#{serviceDegree}
		,#{consultingDegree}
		,#{consultingNm}
		,#{consultingType}
		,#{checkCont}
		,#{consultingCont}
		,#{consultingResult}
		,#{autoinsert}
		,REPLACE(SUBSTR(#{consultingDate},0,10),'-')
		,#{auditInfo}
		,#{sdAppoint}
		,#{rgstrId}
		,#{rgstDt}
		,#{updtrId}
		,#{updtDt}

    )


      SELECT SEQ_TB_DC_CONSULTING.NEXTVAL FROM DUAL

mapper.update:
    UPDATE TB_DC_CONSULTING SET
		VINNO = UPPER(#{vinno})
		,MANAGE_NO = #{manageNo}
		,SERVICE_DEGREE = #{serviceDegree}
		,CONSULTING_DEGREE = #{consultingDegree}
		,CONSULTING_NM = #{consultingNm}
		,CONSULTING_TYPE = #{consultingType}
		,CHECK_CONT = #{checkCont}
		,CONSULTING_CONT = #{consultingCont}
		,CONSULTING_RESULT = #{consultingResult}
		,AUTOINSERT = #{autoinsert}
		,CONSULTING_DATE = REPLACE(SUBSTR(#{consultingDate},0,10),'-')
		,AUDIT_INFO = #{auditInfo}
		,SD_APPOINT = #{sdAppoint}
		,UPDTR_ID = #{updtrId}
		,UPDT_DT = #{updtDt}

    WHERE ID = #{id}
