mapper.select:
SELECT *
FROM (
     SELECT ROWNUM AS RN, A.*
      FROM (




      ) A
      ORDER BY A.ID
 ) AA


  WHERE RN BETWEEN ((${pageable.pageNumber} - 1) * ${pageable.pageSize}) + 1 AND ${pageable.offset}

ORDER BY RN

SELECT COUNT(ID) AS CNT
FROM (




 ) AA

mapper.insert:
    INSERT INTO TB_BA_SUB_CATEGORY (
		ID
		,MAIN_CATEGORY
		,SMALL_CATEGORY
		,AUTOINSERT
		,SMALL_CATEGORY_NM
		,USE_YN
		,PART_DIV
		,RGSTR_ID
		,RGST_DT
		,UPDTR_ID
		,UPDT_DT

    ) VALUES (
		#{id}
		,#{mainCategory}
		,#{smallCategory}
		,#{autoinsert}
		,#{smallCategoryNm}
		,#{useYn}
		,#{partDiv}
		,#{rgstrId}
		,#{rgstDt}
		,#{updtrId}
		,#{updtDt}

    )


      SELECT SEQ_TB_BA_SUB_CATEGORY.NEXTVAL FROM DUAL

mapper.update:
    UPDATE TB_BA_SUB_CATEGORY SET
		MAIN_CATEGORY = #{mainCategory}
		,SMALL_CATEGORY = #{smallCategory}
		,AUTOINSERT = #{autoinsert}
		,SMALL_CATEGORY_NM = #{smallCategoryNm}
		,USE_YN = #{useYn}
		,PART_DIV = #{partDiv}
		,UPDTR_ID = #{updtrId}
		,UPDT_DT = #{updtDt}

    WHERE ID = #{id}
