					close("T");
				}								
				break;
			case "getSZZE203Q" :
				if(ds_SZZE203Q.rowcount <= 0){
					close("B");
				}else{
					close("T");
				}								
				break;
			case "getSVKE110Q" :
				if(ds_SVKE110Q.rowcount <= 0){
					close("B");
				}else{
					close("T");
				}								
				break;
			case "getSZZE103Q" :
				if(ds_SZZE103Q.rowcount <= 0){
					close("B");
				}else{
					close("T");
				}								
				break;
			}
		close();
	}
}

