/********************************************************************** 
 * 개 발 자 : 이상건
 * 내용설명 : 화면 로드
 * 주요변수 : 
 **********************************************************************/ 
function on_LoadCompleted(obj)
{
	ed_select.Text = CODE;
	if (CODE == "zzzzzzzzzz") ed_select.Text="";
	switch (substr(SEL,0,1)){
		case 'A' : 
			title_info.Text = "품번선택"; 
			menuGrd2.SetCellProp("Head", 1, "text", "품번");
			menuGrd2.SetCellProp("Head", 2, "text", "품명");
			menuGrd2.SetCellProp("Head", 3, "text", "ItemType");
			menuGrd2.SetColProp(1,"Width",120);
			menuGrd2.SetColProp(2,"Width",250);
			menuGrd2.SetColProp(3,"Width",50);
			break;
		case 'B' : title_info.Text = "거래처선택"; break;
		case 'C' : 
			title_info.Text = "CODE선택";
			menuGrd2.SetCellProp("Head", 1, "text", "CODE");
			menuGrd2.SetCellProp("Head", 2, "text", "CODE명");
			menuGrd2.SetCellProp("Head", 3, "text", "CODE원명");
			menuGrd2.SetColProp(1,"Width",50);
			menuGrd2.SetColProp(2,"Width",200);
			menuGrd2.SetColProp(3,"Width",250);
			break;
		case 'D' : 
			title_info.Text = "거래처선택"; break;
			menuGrd2.SetCellProp("Head", 1, "text", "거래처");
			menuGrd2.SetCellProp("Head", 2, "text", "거래처명");
			menuGrd2.SetCellProp("Head", 3, "text", "주소");
			menuGrd2.SetColProp(1,"Width",70);
			menuGrd2.SetColProp(2,"Width",200);
			menuGrd2.SetColProp(3,"Width",250);
			break;
		case 'E' : 
			title_info.Text = "업체선택";
			menuGrd2.SetCellProp("Head", 1, "text", "CODE");
			menuGrd2.SetCellProp("Head", 2, "text", "업체코드");
			menuGrd2.SetCellProp("Head", 3, "text", "업체명");
			menuGrd2.SetColProp(1,"Width",50);
			menuGrd2.SetColProp(2,"Width",200);
			menuGrd2.SetColProp(3,"Width",250);
			break;

		case 'F' : title_info.Text = "부서선택"; break;
		case 'G' : title_info.Text = "설비선택"; break;
		case 'H' : title_info.Text = "사원선택"; break;
		case 'I' : title_info.Text = "부품구분선택"; break;
		case 'J' : title_info.Text = "사원정보선택"; break;
		case 'K' : title_info.Text = "작업코드 정보선택"; break;
		case 'L' : title_info.Text = "잔업코드 정보선택"; break;
		case 'M' : title_info.Text = "부서코드 정보선택"; break;
		case 'N' : title_info.Text = "구매거래업체  정보선택"; break;
		case 'O' : title_info.Text = "버스이용지역 정보선택"; break;
		case 'P' : title_info.Text = "작업공정 정보선택"; break;
		case 'Q' : title_info.Text = "보전작업자 선택"; break;
		default : title_info.Text = "";
	}
	imgQuery_OnClick();
}

/********************************************************************** 
 * 개 발 자 : 이상건
 * 내용설명 : 선택별 조회
 * 주요변수 : 
 **********************************************************************/ 
