	// 1=Finished ,A Part	
	// 2=Inhouse 	
	// 3=A-Part,Non-DCJ-Part,DCJ-Part,NON-DCJ-Row-Material
	if (iSelLeft== 'A'){   
		http.Sync 	= true;
		var sKind		= "getItemMaster";		
		var sMethodName = "service::pscomlib/selectItemMasterPopup.do";  					
		var sInDataSet  = "";
		var sOutDataSet = "ds_Select=ds_Select";
		var sArgument   = "";
			sArgument  += " LIB1=" + quote(Lib1);
			sArgument  += " ITNBR=" + quote(ed_select.Text);
			sArgument  += " G_GUB=" + quote(iSelRight);
		Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	}

	// Customer Master (CUSMAS1)
	if (iSelLeft== 'B'){
		http.Sync 	= false;
		var sKind		= "getOGMPrtno";		
		var sMethodName = "service::pscomlib/selectCustomerMasterPopup.do"; 
		var sInDataSet  = "";
		var sOutDataSet = "ds_Select=ds_Select";
