	}
	// Code Master (MST040PF) 제환복 추가
	if (iSelLeft== 'E'){
		http.Sync 	= false;
		var sKind		= "getMngMaster";		
		var sMethodName = "service::pscomlib/selectManagerMasterPopup.do"; 
		var sInDataSet  = "";
		var sOutDataSet = "ds_Select=ds_Select";
		var sArgument   = "";
			sArgument  += " LIB2=" + quote(Lib2);
			sArgument  += " COMPS=" + quote(COMP);
			sArgument  += " MAJCD=" + quote(ed_select.Text);
