	}	
//추가	재환복 2013.01.23
   //작업일보 전용
	if (iSelLeft== 'F'){   //제조부서조회
		http.Sync 	= false;
		var sKind		= "getDPART";		
		var sMethodName = "service::pscomlib/selectDPARTMasterPopup.do"; 
		var sInDataSet  = "";
		var sOutDataSet = "ds_Select=ds_Select";
		var sArgument   = "";
		sArgument += " LIB2=" + quote(Lib2);
		sArgument += " COMPS=" + quote(COMPS); //사업부
		sArgument += " DPCODE=" + quote(ed_select.Text); //부서코드
		Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	}
	if (iSelLeft== 'H'){ //인사마스터 조회
		http.Sync 	= false;
		var sKind		= "getINSA";		
		var sMethodName = "service::pscomlib/selectINSAMasterPopup.do"; 
		var sInDataSet  = "";
		var sOutDataSet = "ds_Select=ds_Select";
		var sArgument   = "";
			sArgument  += " LIB2=" + quote(Lib2);
			//sArgument  += " LIB3=" + quote(Lib2);
			sArgument  += " LIB3=" + quote(Lib3);
			sArgument  += " COMPS=" + quote(COMPS);
			sArgument  += " KORNM=" + quote(ed_select.Text);
			sArgument  += " PAYCD=" + quote(PAYCD); //paycd값이 1이면 기능직 2이면 직반장
		Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	}
	if (iSelLeft== 'K'){ //작업코드 조회
		http.Sync 	= false;
		var sKind		= "getJOBCD";		
		var sMethodName = "service::pscomlib/selectJOBCodePopup.do"; 
		var sInDataSet  = "";
		var sOutDataSet = "ds_Select=ds_Select";
		var sArgument   = "";
			sArgument  += " LIB2=" + quote(Lib2);
			sArgument  += " COMPS=" + quote(COMPS);
			sArgument  += " JOBCD=" + quote(ed_select.Text);
		Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	}
	if (iSelLeft== 'L'){ //잔업코드 조회
		http.Sync 	= false;
		var sKind		= "getOVERTIME";		
		var sMethodName = "service::pscomlib/selectOVERTIMEPopup.do"; 
		var sInDataSet  = "";
		var sOutDataSet = "ds_Select=ds_Select";
		var sArgument   = "";
			sArgument  += " LIB2=" + quote(Lib2);
			sArgument  += " COMPS=" + quote(COMPS);
			sArgument  += " SPSNM=" + quote(ed_select.Text);
		Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	}
	if (iSelLeft== 'M'){ //MST650PF부서코드 조회
