			sArgument  += " ZIPNM=" + quote(ed_select.Text);//동이름
			//sArgument  += " DPEDSW=" + quote("T"); 사용여부
		Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	}
	if (iSelLeft== 'P'){//작업공정정보마스타
		http.Sync 	= false;
		var sKind		= "getMST060PF";		
		var sMethodName = "service::pscomlib/selectMST060PFPopup.do"; 
		var sInDataSet  = "";
		var sOutDataSet = "ds_Select=ds_Select";
		var sArgument   = "";
			sArgument  += " LIB2=" + quote(Lib2);
			sArgument  += " COMPS=" + quote(COMPS);
			sArgument  += " PROCS=" + quote(ed_select.Text);//공정코드
		Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	}
	if (iSelLeft== 'Q'){//보전작업자마스터
		http.Sync 	= false;
		var sKind		= "getOBMZ160PF";		
		var sMethodName = "service::pscomlib/selectOBMZ160PFPopup.do"; 
		var sInDataSet  = "";
		var sOutDataSet = "ds_Select=ds_Select";
		var sArgument   = "";
			sArgument  += " LIB2=" + quote(Lib2);
			sArgument  += " COMPS=" + quote(P_COMPS);
			sArgument  += " KORNM=" + quote(ed_select.Text);//이름
