		http.Sync 	= false;
		var sKind		= "getOGMMaker";		
		var sMethodName = "service::psogmlib/selectOGMMaker.do";  					
		var sInDataSet  = "";
		var sOutDataSet = "ds_Select=ds_Select";
		var sArgument   = "";
		sArgument += " LIB2=" + quote(Lib2);
		sArgument += " COMPS=" + quote(COMPS); //사업부
		sArgument += " GROUP=" + quote(GROUP); //그룹
		sArgument += " MAKER=" + quote(ed_select.Text); //maker code
		Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	}
	if (iSel== 5){   //사용자
		http.Sync 	= false;
		var sKind		= "getOGMUSER_ID";		
		var sMethodName = "service::psogmlib/selectOGMUserID.do";  					
		var sInDataSet  = "";
		var sOutDataSet = "ds_Select=ds_Select";
		var sArgument   = "";
		sArgument += " COMPS=" + quote(COMPS); //사업부
		sArgument += " USER_ID=" + quote(ed_select.Text); //디오네 사용자 code
