		Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	}
	if (iSel== 6){   //부서
		http.Sync 	= false;
		var sKind		= "getOGMPrtcd";		
		var sMethodName = "service::psogmlib/selectOGMPrtcd.do";  					
		var sInDataSet  = "";
		var sOutDataSet = "ds_Select=ds_Select";
		var sArgument   = "";
		sArgument += " LIB2=" + quote(Lib2);
		sArgument += " COMPS=" + quote(COMPS); //사업부
		sArgument += " GROUP=" + quote(GROUP); //그룹
		sArgument += " PRTCD=" + quote(ed_select.Text); //부서코드
		Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
