		sArgument += " COMPS=" + quote(COMPS); //사업부
		sArgument += " GROUP=" + quote(GROUP); //그룹
		sArgument += " MSHNO=" + quote(ed_select.Text); //설비번호
		Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	}
	if (iSel== 9){   //부품구분
		http.Sync 	= false;
		var sKind		= "getOGMMstcd";		
		var sMethodName = "service::psogmlib/selectOGMMstcd.do";  					
