Window.Script:
function fn_FormLoadSetting(obj){

	//Timer Setting
	SetTimer(1, 100);

	//Show Status가 False
	if (GBL_PROGRESSCHK==false) {
		Close();
		return;
	}

}

function fn_FormUnloadProcess(obj){

	//Form Unload時 Timer Kill
	KillTimer(1);

}

function fn_DoLoop(){

	//Show Status가 False
	if (GBL_PROGRESSCHK==false) {
		Close();
		return;
	}

}
