function onLoadcompleted(obj)
{


	Static2.Text = G_ds_user.GetColumn(0,"USER_NAME") + "님 반갑습니다.";
	if(G_ds_user.GetColumn(0,"COMPANY") == "PS"){
		Image0.ImageID = "top_img8";
	}else if(G_ds_user.GetColumn(0,"COMPANY") == "PE"){
		Image0.ImageID = "top_img9";
	}else {
		Image0.ImageID = "top_img(DIKR)";
	}

	getUserNotice();
	viewUserNotice();
	gFcOnTopButtonColor("0"); //상단 버튼 활성화 여부 (기본:0,조회:1,변경:2)		
}



// 버튼권한 체크
function btnAuthCheck(btnId)
{
	http.sync = true;
	var sParam = "";

	if(GetTopWindow().id == "MDIForm0"){
		return false ;
	}

	sParam += " USER_ID=" + quote(G_ds_user.GetColumn(0,"USER_ID")); // 필수 인자
	sParam += " MENU_ID=" + quote(GetTopWindow().div_content.sMenuId); // 필수 인자

	Transaction(
		"btnAuth",								// Transaction ID
		"service::login/btnAuth.do",			// service
		"",										// input Dataset
		"ds_btnAuth=ds_btnAuth",
		sParam,             					// parameter
		""					// callback
	);

	if(ds_btnAuth.GetColumn(0,"BTN" +btnId) != "1"){

		alert(setPopMessage("1007"));
		setMessage("1007");
		return false ;
	}  
	return true;


}



function getUserNotice()
{
	http.sync = true;
	var sParam = "";

	var nowDate = today();	

	sParam += " TODAY=" + quote(nowDate);	
	sParam += " COMPANY=" + quote(G_ds_user.GetColumn(0,"COMPANY"));
	sParam += " PLANT_CD=" + quote(G_ds_user.GetColumn(0,"PLANT_CD"));

	Transaction(
		"getUserNotice",								// Transaction ID
		"service::login/selectUserNotice.do",			// service
		"",												// input Dataset
		"ds_userNotice=ds_userNotice",
		sParam,             							// parameter
		""												// callback
	);
}


function viewUserNotice()
{

	if(ds_userNotice.rowcount != 0){
		var str = Open("Common::PopNotice.xml","",100,100,"Taskbar=true resize=false AutoSize=true",210,90);
	}


	/*
	var cab = 0; 
	for(i=0;i<ds_userNotice.rowcount;i++){
		cab = cab + 200;
		var str = Open("Common::PopNotice.xml","NTC_SEQ=" + ds_userNotice.GetColumn(i,"NTC_SEQ"),100,100,"Taskbar=true resize=false AutoSize=true",210 + cab,90 + cab);
	}
	*/
}



// 조회
function gCommonSearch(obj)
{
	//MRP시스템 활성화관리
	//1 :정상, 2: 야간배치, 3:실사
	if(G_ds_topbutton.GetColumn(0,"SEARCH") != "disable"){
		getMngFlag();//MPR시스템 운영FLAG
		getMenuGroup();
		var pgmUSETF = substr(ds_menuGroup.GetColumn(0,"TOP_MENU"),0,1);
		if (ds_mngflag.GetColumn(0,"USER_YN") = 0) {
			if(btnAuthCheck("1") == false) return;
			GetTopWindow().div_content.fn_Search(); 
		}else if ((ds_mngflag.GetColumn(0,"USER_YN") = 8 || ds_mngflag.GetColumn(0,"USER_YN") = 9 )&& pgmUSETF!="m") {
			if(btnAuthCheck("1") == false) return;
			GetTopWindow().div_content.fn_Search(); 
		}else if (ds_mngflag.GetColumn(0,"USER_YN") = 8 && pgmUSETF=="m") {
			alert(setPopMessage("9997"));//[알림]야간배치진행중으로 사용불가능합니다.
			setMessage("9997");
			getLeft();//메인메뉴로 이동
			GetTopWindow().close();//활성화된 창닫기
			return;
		}
		else if (ds_mngflag.GetColumn(0,"USER_YN") = 9  && pgmUSETF=="m") {
			alert(setPopMessage("9998"));//[알림]실사진행중으로 사용불가능합니다.
			setMessage("9998");//[알림]실사진행중으로 사용불가능합니다.
			getLeft();//메인메뉴로 이동
			GetTopWindow().close();//활성화된 창닫기
			return;
		}
	}
}


