function Button6_OnMouseOver(obj,nX,nY)
{
	Button6.ImageStatus = "select";						
}

function getLeft(){
	var wi = -1;
	while (wi < global.windows.count){
	wi++;
		if (global.windows[wi].id == "FrameLeft") {
			global.windows[wi].Div0_Button0_OnClick();
			return; 
		} 
	} 
}
function getMenuGroup(){
	http.Sync = true;
	var sKind		= "getMenuGroup";						
	var sMethodName = "service::user/selectMenuList.do";     	// 서버에서 호출 될 Method 명
	var sInDataSet  = "";                             			// 자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet = "ds_menuGroup=ds_menuList";    			// 조회 후 돌려 받을 DataSet 명칭
	var sArgument   = "";
		sArgument   += " URL=" + quote(GetTopWindow().id);
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	http.Sync = false;
}
