	searchBar.Height = G_ds_design.GetColumn(0,"SBHeight");	
	searchBar.Top = G_ds_design.GetColumn(0,"SBTop");	

}

function getComCode(){
	http.Sync 	= true;

	var sKind		= "getComCode";						
	var sMethodName = "service::login/selectComCode.do"; 
	var sInDataSet  = "";
	var sOutDataSet = "ds_ComCode_PLNT=ds_ComCode_PLNT";
	var sArgument   = "";
		sArgument   += " CLASS='PLNT'";
		sArgument   += " CODE1=" + quote(company);		
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	//ds_VComCode_PLNT.Copy("ds_ComCode_PLNT");
	//cb_searchCodeName1.Value = comps;
	ds_ComCode_PLNT.InsertRow(0);
	ds_ComCode_PLNT.SetColumn(0,"CODE2","");
	ds_ComCode_PLNT.SetColumn(0,"CODE_NAME","[전체]");
	cb_searchCodeName1.Index = 0;
}
function getDWH000(){
	http.Sync 	= true;
	var sKind		= "getDWHZ000Q";						
	var sMethodName = "service::dwhlib/selectDWHZ000Q.do"; 
	var sInDataSet  = "";
	var sOutDataSet = "ds_DWHZ000Q=ds_DWHZ000Q";
	var sArgument   = "";
		sArgument  += " LIB2=" + quote(Lib2);
		sArgument  += " JOBFL=" + quote(jobpfnm);
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	http.Sync 	= false;
	ca_RVSDT.Value = ds_DWHZ000Q.GetColumn(0,"JYYMM")+"01";
}
/********************************************************************** 
 * 내용설명 : 화면조회
 * 주요변수 : 
 **********************************************************************/ 
function getDWHC157Q()
{
	if(length(trim(ca_RVSDT.Value)) < 8){
		alert(setPopMessage("2016"));  //[알림] 날짜를 입력하세요.
		setMessage("2016");	
		ca_RVSDT.SetFocus();
		return;
	}	

	ds_DWHXXXYQ.ClearData();
	//Dataset 생성
	for(step=1 ;step<=21;step++){
		var dsObjParam = find("ds_DWHXXXYQ_"+step);
		if ( dsObjParam == null || dsObjParam == "") {
			Create("Dataset", "ds_DWHXXXYQ_"+step);
		}
	}
	var step;
	for(step=1 ;step<21;step++){
		var start_rownumber=0;
		var end_rownumber=0;
		switch (step){
		case 1 : 
			start_rownumber = 1;	end_rownumber=10000;	break;
		case 2 : 
			start_rownumber = 10001;	end_rownumber=20000;	break;
		case 3 : 
			start_rownumber = 20001;	end_rownumber=30000;	break;
		case 4 : 
			start_rownumber = 30001;	end_rownumber=40000;	break;
		case 5 : 
			start_rownumber = 40001;	end_rownumber=50000;	break;
		case 6 : 
			start_rownumber = 50001;	end_rownumber=60000;	break;
		case 7 : 
			start_rownumber = 60001;	end_rownumber=70000;	break;
		case 8 : 
			start_rownumber = 70001;	end_rownumber=80000;	break;
		case 9 : 
			start_rownumber = 80001;	end_rownumber=90000;	break;
		case 10 : 
			start_rownumber = 90001;	end_rownumber=100000;	break;
		case 11 : 
			start_rownumber = 100001;	end_rownumber=110000;	break;
		case 12 : 
			start_rownumber = 110001;	end_rownumber=120000;	break;
		case 13 : 
			start_rownumber = 120001;	end_rownumber=130000;	break;
		case 14 : 
			start_rownumber = 130001;	end_rownumber=140000;	break;
		case 15 : 
			start_rownumber = 140001;	end_rownumber=150000;	break;
		case 16 : 
			start_rownumber = 150001;	end_rownumber=160000;	break;
		case 17 : 
			start_rownumber = 160001;	end_rownumber=170000;	break;
		case 18 : 
			start_rownumber = 170001;	end_rownumber=180000;	break;
		case 19 : 
			start_rownumber = 180001;	end_rownumber=190000;	break;
		case 20 : 
			start_rownumber = 190001;	end_rownumber=200000;	break;
		}
		var sKind		= "getDWHC157Q_"+step;						
		var sMethodName = "service::dwhlib/selectDWHXXXYQ.do";     		// 서버에서 호출 될 Method 명
		var sInDataSet  = "";                             				// 자료 저장 시 보낼 DataSet 명칭
		var sOutDataSet = "ds_DWHXXXYQ_"+step+"=ds_DWHXXXYQ_"+step;    				// 조회 후 돌려 받을 DataSet 명칭
		var sArgument   = "";
		sArgument += " LIB1=" + quote(Lib1);
		sArgument += " LIB2=" + quote(Lib2);
		sArgument += " JOBFL=" + quote(jobpfnm);
		sArgument += " STEP=" + quote(step);
		sArgument += " F_ROWNUM=" + quote(start_rownumber);
		sArgument += " T_ROWNUM=" + quote(end_rownumber);	
		http.Sync = true;
		Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "callback");
		http.Sync = false;
		if(object("ds_DWHXXXYQ_"+step).rowcount != 10000){
			step=21;
		}
	}
	//Dataset Append (ds_DWHXXXYQ)
	for(step=1 ;step<21;step++){
		if (object("ds_DWHXXXYQ_"+step).rowcount > 0) ds_DWHXXXYQ.AppendDataset("ds_DWHXXXYQ_"+step);
		object("ds_DWHXXXYQ_"+step).ClearData();
	}
}

/********************************************************************** 
 * 내용설명 : 엑셀파일로 저장
 * 주요변수 : 
 **********************************************************************/ 
function setExcel()
{
	var fName   = "제품입출고현황"+substr(ca_RVSDT.Value,0,6);
	var arrSheets  =  Array();  

	arrSheets[0]  = "MainGrid|제품입출고현황";
	createExcelSheetS2(FileDialog0, fName ,arrSheets);

}


/********************************************************************** 
 * 내용설명 : Transaction 리턴후 실행 함수
 * 주요변수 : 
 * 			- sMethodName	Transaction 고유 ID
 * 			- ErrorCode 	에러코드
 * 			- ErrorMSG 		에러 메세지
 **********************************************************************/ 
function callback(svcId, rtnCd, rtnMsg){
	// tran 실패
	if(rtnCd<0){
		alert(setPopMessage("1002"));
		setMessage("1002");
	}
}

