	                          //                       2.1:조회,추가,삭제,저장     2.2:조회,추가,삭제,저장,excel       2.3:조회,저장,excel
	                          //                       2.4:조회,추가,저장          2.5:저장                            2.6:조회,삭제,저장
	                          //                       2.7:조회,추가,저장,excel 	


	MainGrid.UserData = MainGrid.Height;
}


/********************************************************************** 
 * 내용설명 : 공통코드 조회
 * 주요변수 : 
 **********************************************************************/ 
function getDesign(){
	MainGrid.ToolTipText = setPopMessage("2053");
	MainGrid.BkColor = G_ds_design.GetColumn(0,"BkColor");
	MainGrid.BkColor2 = G_ds_design.GetColumn(0,"BkColor2");
	MainGrid.BkSelColor = G_ds_design.GetColumn(0,"BkSelColor");
	MainGrid.Face3dColor = G_ds_design.GetColumn(0,"Face3dColor");
	MainGrid.BoldHead = G_ds_design.GetColumn(0,"BoldHead");
	MainGrid.HeadHeight = G_ds_design.GetColumn(0,"HeadHeight");
	MainGrid.Width = G_ds_design.GetColumn(0,"Width");
	MainGrid.Top = G_ds_design.GetColumn(0,"Top");

	searchBar.Width = G_ds_design.GetColumn(0,"Width");
	searchBar.Height = G_ds_design.GetColumn(0,"SBHeight");	
	searchBar.Top = G_ds_design.GetColumn(0,"SBTop");	

}

function getComCode(){
	http.Sync 	= true;

	var sKind		= "getComCode";						
	var sMethodName = "service::login/selectComCode.do"; 
	var sInDataSet  = "";
	var sOutDataSet = "ds_ComCode_PLNT=ds_ComCode_PLNT";
	var sArgument   = "";
		sArgument   += " CLASS='PLNT'";
		sArgument   += " CODE1=" + quote(company);		
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	cb_searchCodeName1.Value=comps;
	if (cb_searchCodeName1.Value == "C1"){
		Lib1 = "PSRESLIB";
		Lib2 = "PSCDBLIB";
		Lib3 = "PSCDBLIB";
	} else if(cb_searchCodeName1.Value == "H1"){
		Lib1 = "PSRESLIB";
		Lib2 = "PSCDBLIB";
		Lib3 = "PSHDBLIB";
	} else if(cb_searchCodeName1.Value == "S1"){
		Lib1 = "PSRESLIB";
		Lib2 = "PSCDBLIB";
		Lib3 = "PSSDBLIB";
	}
	//ds_VComCode_PLNT.Copy("ds_ComCode_PLNT");
	//cb_searchCodeName1.Value = comps;
	//ds_ComCode_PLNT.InsertRow(0);
	//ds_ComCode_PLNT.SetColumn(0,"CODE2","");
	//ds_ComCode_PLNT.SetColumn(0,"CODE_NAME","[전체]");
	//cb_searchCodeName1.Index = 0;
}
function getDWH000(){
	http.Sync 	= true;
	var sKind		= "getDWHZ000Q";						
	var sMethodName = "service::dwhlib/selectDWHZ000Q.do"; 
	var sInDataSet  = "";
	var sOutDataSet = "ds_DWHZ000Q=ds_DWHZ000Q";
	var sArgument   = "";
		sArgument  += " LIB2=" + quote(Lib2);
		sArgument  += " JOBFL=" + quote(jobpfnm);
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	http.Sync 	= false;
	ca_RVSDT.Value = ds_DWHZ000Q.GetColumn(0,"JYYMM")+"01";
}
/********************************************************************** 
 * 내용설명 : 화면조회
 * 주요변수 : 
 **********************************************************************/ 
function getDWHC505Q()
{
	if(length(trim(ca_RVSDT.Value)) < 8){
		alert(setPopMessage("2016"));  //[알림] 날짜를 입력하세요.
		setMessage("2016");	
		ca_RVSDT.SetFocus();
		return;
	}	

	var sKind		= "getDWH";						
	var sMethodName = "service::dwhlib/selectManagement.do";     		// 서버에서 호출 될 Method 명
	var sInDataSet  = "";                             				// 자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet = "ds_DWH=ds_DWH";    				// 조회 후 돌려 받을 DataSet 명칭
	var sArgument   = "";
	sArgument += "@LIB2=" + quote(Lib2);
	sArgument += "@LIB3=" + quote(Lib3);
	sArgument += "@COMPS=" + quote(cb_searchCodeName1.Value);
	sArgument += "@RVSDT=" + quote(substr(ca_RVSDT.Value,0,6)); //년월
	sArgument += "@SQLID=" + "selectDWHC505Q";
	var str = dialog("Common::PGMLOAD_CST.xml","sKind=" + sKind + " " + "sMethodName=" + sMethodName + " " + "sOutDataSet=" + sOutDataSet + " " + "sArgument=" + sArgument,272,64,"TitleBar=false");	
	//Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "callback");
	if (str == 'T'){  //정상조회
		alert(setPopMessage("1004"));
		setMessage("1004");	
	}else if (str == 'B'){   //data값이 없을때
		alert(setPopMessage("1003"));
		setMessage("1003");	
	}else if (str == 'E'){   //error발생시
		alert(setPopMessage("1002"));
		setMessage("1002");	
	}
}

/********************************************************************** 
 * 내용설명 : 엑셀파일로 저장
 * 주요변수 : 
 **********************************************************************/ 
function setExcel()
{
	var fName   = "입퇴휴직자현황"+substr(ca_RVSDT.Value,0,6);
	var arrSheets  =  Array();  

	arrSheets[0]  = "MainGrid|입퇴휴직자현황";
	createExcelSheetS2(FileDialog0, fName ,arrSheets);

}


/********************************************************************** 
 * 내용설명 : Transaction 리턴후 실행 함수
 * 주요변수 : 
 * 			- sMethodName	Transaction 고유 ID
 * 			- ErrorCode 	에러코드
 * 			- ErrorMSG 		에러 메세지
 **********************************************************************/ 
function callback(svcId, rtnCd, rtnMsg){
	// tran 실패
	if(rtnCd<0){
		alert(setPopMessage("1002"));
		setMessage("1002");
	}
}

