function cb_searchCodeName1_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	if (strCode == "C1"){
		Lib1 = "PSRESLIB";
		Lib2 = "PSCDBLIB";
		Lib3 = "PSCDBLIB";
	} else if(strCode == "H1"){
		Lib1 = "PSRESLIB";
		Lib2 = "PSCDBLIB";
		Lib3 = "PSHDBLIB";
	} else if(strCode == "S1"){
		Lib1 = "PSRESLIB";
		Lib2 = "PSCDBLIB";
		Lib3 = "PSSDBLIB";
	}
	ds_DWH.ClearData();
}
