/********************************************************************** 
 * 내용설명 : 공통디자인 설정
 * 주요변수 : 
 **********************************************************************/ 
function getDesign(){
	//MainGrid.ToolTipText = setPopMessage("2053");
	MainGrid.BkColor = G_ds_design.GetColumn(0,"BkColor");
	MainGrid.BkColor2 = G_ds_design.GetColumn(0,"BkColor2");
	MainGrid.BkSelColor = G_ds_design.GetColumn(0,"BkSelColor");
	MainGrid.Face3dColor = G_ds_design.GetColumn(0,"Face3dColor");
	MainGrid.BoldHead = G_ds_design.GetColumn(0,"BoldHead");
	//MainGrid.HeadHeight = G_ds_design.GetColumn(0,"HeadHeight");  //22
	MainGrid.Width = G_ds_design.GetColumn(0,"Width");            //1048
	MainGrid.Top = G_ds_design.GetColumn(0,"Top");                //64

	searchBar.Width = G_ds_design.GetColumn(0,"Width");           //1048
	searchBar.Height = G_ds_design.GetColumn(0,"SBHeight");	      //30
	searchBar.Top = G_ds_design.GetColumn(0,"SBTop");	          //32
}

/********************************************************************** 
 * 내용설명 : 사업부 선택
 * 주요변수 : 
 **********************************************************************/ 
function getComCode(){
	http.Sync 	= true;

	var sKind		= "getComCode";						
	var sMethodName = "service::login/selectComCode.do"; 
	var sInDataSet  = "";
	var sOutDataSet = "ds_ComCode_PLNT=ds_ComCode_PLNT";
	var sArgument   = "";
		sArgument   += " CLASS='PLNT'";
		sArgument   += " CODE1=" + quote(company);		
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	//ds_VComCode_PLNT.Copy("ds_ComCode_PLNT");
	cb_searchCodeName1.Value = comps;

}
/********************************************************************** 
 * 내용설명 : 화면조회
 * 주요변수 : 
 **********************************************************************/ 
function getDWH(){

	ds_DWH.Clear();

	if(length(trim(ca_START.Value)) < 8){
		alert(setPopMessage("4674"));
		setMessage("4674");
		ca_START.SetFocus();
		return;
	}

	//Paging처리
	var sKind		= "getDWH";						
	var sMethodName = "service::dwhlib/selectManagement.do";     		// 서버에서 호출 될 Method 명
	var sInDataSet  = "";                             				// 자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet = "ds_CNT=ds_DWH";    				// 조회 후 돌려 받을 DataSet 명칭
	var sArgument   = "";
		sArgument += " LIB1=" + quote(Lib1);
		sArgument += " CONDATE=" + quote(ca_START.Value);
		sArgument += " SQLID=" + "selectdwhz043qCnt";
	    sArgument += " STARTDATE=" + quote(ca_START.Value);	
	    sArgument += " ENDDATE=" + quote(ca_END.Value);
	http.Sync = true;
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	http.Sync = false;

	var dsCnt=0;
	var rowCnt=0;
		dsCnt =parseInt(ds_CNT.GetColumn(0,"CNT"));
	if(dsCnt>0){
		rowCnt=ceil(dsCnt/10000);
	}
	var pCnt_F=0;
	var pCnt_T=0;

	for(i=0; i<rowCnt; i++){
		if( i==0){
			pCnt_F =1;
			pCnt_T =10000; 
		}else{
			pCnt_F = (i*10000)+1; 
			pCnt_T = (i+1)*10000; 
		}

			var sKind		= "getDWH";						
			var sMethodName = "service::dwhlib/selectManagement.do";     		// 서버에서 호출 될 Method 명
			var sInDataSet  = "";                             				// 자료 저장 시 보낼 DataSet 명칭
			var sOutDataSet = "ds_DWH_SUB=ds_DWH";    				// 조회 후 돌려 받을 DataSet 명칭
			var sArgument   = "";
			sArgument += "@LIB1=" + quote(Lib1);
			sArgument += "@LIB2=" + quote(Lib2);
			sArgument += "@COMPS=" + quote(iif(cb_searchCodeName1.Value == "PE","E1","C1"));
			sArgument += "@SQLID=" + "selectdwhz043q";	
			sArgument += "@CONDATE=" + quote(ca_START.Value);
	        sArgument += "@STARTDATE=" + quote(ca_START.Value);	
	        sArgument += "@ENDDATE=" + quote(ca_END.Value);
			sArgument += "@STARTNUM=" + quote(pCnt_F);
			sArgument += "@ENDNUM=" +  quote(pCnt_T);

			var str = dialog("Common::PGMLOAD_CST.xml","sKind=" + sKind + " " + "sMethodName=" + sMethodName + " " + "sOutDataSet=" + sOutDataSet + " " + "sArgument=" + sArgument,272,64,"TitleBar=false");	
			//Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "callback");
			if (str == 'T'){  //정상조회
				alert(setPopMessage("1004"));
				setMessage("1004");	
			}else if (str == 'B'){   //data값이 없을때
				alert(setPopMessage("1003"));
				setMessage("1003");	
			}else if (str == 'E'){   //error발생시
				alert(setPopMessage("1002"));
				setMessage("1002");	
			}

			ds_DWH.AppendDataset(ds_DWH_SUB);			
			ds_DWH_SUB.Clear();
	}

}


/********************************************************************** 
 * 내용설명 : 엑셀파일로 저장
 * 주요변수 : 
 **********************************************************************/ 
function setExcel()
{
	var fName   = "국내 구매단가 정단가화 리스트";
	var arrSheets  =  Array();  

	arrSheets[0]  = "MainGrid|국내 구매단가 정단가화 리스트";
	createExcelSheetS2(FileDialog0, fName ,arrSheets);
}

/********************************************************************** 
 * 내용설명 : Transaction 리턴후 실행 함수
 * 주요변수 : 
 * 			- sMethodName	Transaction 고유 ID
 * 			- ErrorCode 	에러코드
 * 			- ErrorMSG 		에러 메세지
 **********************************************************************/ 
function callback(svcId, rtnCd, rtnMsg){
	// tran 실패
	if(rtnCd<0){
		alert(setPopMessage("1002"));
		setMessage("1002");

	}else if(svcId=="getDWH"){
		// 조회건수가 없을경우
		if(ds_dwh.rowcount <= 0){
			alert(setPopMessage("1003"));
			setMessage("1003");	
		// 정상 조회
		} else {
			alert(setPopMessage("1004"));
			setMessage("1004");				
		}
	}
}

function Grid0_OnHeadClick(obj,nCell,nX,nY,nPivotIndex)
{
	if(nCell <= 9) lib_grid_SetGridSort(obj,nCell);
}

function Grid0_OnKeyDown(obj,nChar,bShift,bCtrl,bAlt,nLLParam,nHLParam)
{
	// Ctrl + C
	if(bCtrl == 1 && nChar == 67) {

		// grd_Standard.AreaSelect = true;
		grd_fn_ClipboardCopy(obj, strSeparator);

	}
}

