function on_LoadCompleted(obj)
{

	getTitle();
	ds_fileType.row = 0;
}


/********************************************************************** 
 * 개 발 자 : 황규철 
 * 개발일자 : 2010-11-26
 * 내용설명 : 메인 그리드 클릭 이벤트
 * 주요변수 : 
 **********************************************************************/ 
function onCellCilck(obj,nRow,nCell,nX,nY,nPivotIndex)
{

	ApplyData();

	var tableNm = ds_list.GetColumn(nRow,"tableNm");
	var gridNm 	= ds_list.GetColumn(nRow,"gridNm");
	var yyyymm  = ds_list.GetColumn(nRow,"date");
	var fileType  = ds_list.GetColumn(nRow,"fileType");
	var ntitle  = ds_list.GetColumn(nRow,"title");	

	if(nCell == 4){

		if(ds_list.GetColumn(nRow,"date") == ""){
			alert(setPopMessage("2016"));	// 20110103l-koowgnojeel, 2016 == 날짜를 입력하세요.		
			return ;		
		}

		if(isDate(ds_list.GetColumn(nRow,"date"))==false){
			alert(setPopMessage("2008"));	// 20110103l-koowgnojeel, 2008 == 날짜 형식이 아닙니다.
			return ;
		}

		if(fileType == "1"){
			// 다운로드할 데이터셋 호출
			getDataSet(tableNm,yyyymm);
			// 엑셀파일 다운로드
			saveExcel(gridNm,ntitle,yyyymm);
		} else if (fileType == "2") {
			// 다운로드할 데이터셋 호출
			getDataSet(tableNm,yyyymm);	
			//  텍스트파일 다운로드
			saveText(gridNm,ntitle,yyyymm);	
		} else {
			alert(setPopMessage("2015"));	// 20110103l-koowgnojeel, 2015 == 파일형식을 선택하세요.
		}
	}  
}


/********************************************************************** 
 * 개 발 자 : 황규철 
 * 개발일자 : 2010-11-26
 * 내용설명 : 다운로드할 데이터셋 조회
 * 주요변수 : 
 * 			- tableNm	조회할 테이블명
 * 			- yyyymm 	조회 조건
 **********************************************************************/ 
function getDataSet(tableNm,yyyymm)
{
	//ds_main.DeleteAll();

	ds_main.Clear();

	http.Sync 		= true;
	var sKind		= "SEARCH00";						
	var sMethodName = "service::sample/selectDownLoad.do";
	var sInDataSet  = "";
	var sOutDataSet = "ds_main=ds_main";
	var arg			= " LIB=" + quote(Lib) + " TABLENM=" + quote(tableNm) + " YYYYMM=" + quote(yyyymm);

	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, arg, "");	

}


/********************************************************************** 
 * 개 발 자 : 황규철 
 * 개발일자 : 2010-11-26
 * 내용설명 : 엑셀로 저장
 * 주요변수 : 
 * 			- tableNm	조회할 테이블명
 * 			- ntitle 	파일명
 * 			- yyyymm 	조회 조건
 **********************************************************************/ 
function saveExcel(gridNm,ntitle,yyyymm)
{

	FileDialog0.FileName = ntitle + "(" + substr(yyyymm,0,6) + ")" + ".xls";
	FileDialog0.Filter = "ALL File(xls.*)|*.xls";
	FileDialog0.Type = "SAVE";

	if (!FileDialog0.Open()) {
		//alert("저장경로를 선택하는데 실패하였습니다.");
		return;
	}

	var path = FileDialog0.FilePath + "\\" + FileDialog0.FileName;
	setMessageDirect(path);	
	object(gridNm).SaveExcel(path,"sheet1","A1",false,true);

}

/********************************************************************** 
 * 개 발 자 : 황규철 
 * 개발일자 : 2010-11-26
 * 내용설명 : TEXT파일로 저장
 * 주요변수 : 
 * 			- tableNm	조회할 테이블명
 * 			- ntitle 	파일명
 * 			- yyyymm 	조회 조건
 **********************************************************************/ 
function saveText(gridNm,ntitle,yyyymm)
{
	FileDialog0.FileName = ntitle + "(" + substr(yyyymm,0,6) + ")" + ".txt";
	FileDialog0.Filter = "ALL File(txt.*)|*.txt";
	FileDialog0.Type = "SAVE";
	if (!FileDialog0.Open()) {
		//alert("저장경로를 선택하는데 실패하였습니다.");
		return;
	}

	var path = FileDialog0.FilePath + "\\" + FileDialog0.FileName;
	setMessageDirect(path);		

	file.FileName = path;
	file.open("wt");

	var buff = "";

	var str = dialog("Group1::PROGRESSBAR.xml","gridNm=" + gridNm ,400,190,"TitleBar=ture,CloseFlag=false");	

	file.Close();



	/*

	for(i=0; i<ds_main.rowcount; i++)
	{
		for(j=0; j<ds_main.colcount; j++)
		{
			//RPad(strString,strPadChar,nCount);
			//buff += ds_main.GetColumn(i,j);
			buff += Object(gridNm).GetCellValue(i,j);			
		}
		buff += "\r\n";
	}
	file.Write(buff); 
	*/


	/*
	RPad(strString,strPadChar,nCount);

	▣ Parameters 
	strString (String) : 원본 문자열. 
	strPadChar (String) : 오른쪽에 채울 문자. 입력된 문자열의 첫 1글자만을 사용합니다. 
	nCount (Integer) : 출력될 문자열의 길이. 
	ex)Rpad("ab", "?", 4);	
	*/
}


/********************************************************************** 
 * 개 발 자 : 황규철 
 * 개발일자 : 2010-11-26
 * 내용설명 : Transaction 리턴후 실행 함수
 * 주요변수 : 
 * 			- sMethodName	Transaction 고유 ID
 * 			- ErrorCode 	에러코드
 * 			- ErrorMSG 		에러 메세지
 **********************************************************************/ 
function fn_CallBack(sMethodName, ErrorCode, ErrorMSG) {
	if(ErrorCode != 0)
	{
		returnMSG = ErrorMSG;
	} else {
		returnMSG = "처리되었습니다.                 ";
	}
}




