#include "script::lib_script_common.js";

function on_LoadCompleted(obj){	
	getTitle();	
	getComCode();
	search_trndt = substr(today(), 0,4);	
}

function getComCode(){

	http.Sync 	= true;
	var sKind		= "getComCode";						
	var sMethodName = "service::login/selectComCode.do"; 
	var sInDataSet  = "";
	var sOutDataSet = "ds_ComCode_PLNT=ds_ComCode_PLNT";
	var sArgument   = "";
		sArgument   += " CLASS='PLNT'";
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");

	ds_ComCode_PLNT.Filter("code2=='" + G_ds_user.GetColumn(0,"PLANT_CD") +"'");	
	http.Sync 	= false;
}

function getChartList(){
	var sKind		= "getChartList";						
	var sMethodName = "service::chart/selectChart02.do";     // 서버에서 호출 될 Method 명
	var sInDataSet  = "";                             // 자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet = "ds_chart0=ds_chart0 ds_chart1=ds_chart1";    // 조회 후 돌려 받을 DataSet 명칭
	var sArgument   = "";
	//sArgument += " COMPS=" + quote(search_comps.Value);
	sArgument += " PRTYM=" + quote(search_trndt.Value);
	sArgument += " PSPNO=" + quote(search_pspno.Text);	

	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "callback");
}

function loadingChart0(){
	xChart0.Titles[0].Text = "제품별 점유율";

	xChart0.OpenData("COD_Values", 1, ds_chart0.rowCount());	
	for(var i=0; i<ds_chart0.rowCount(); i++){	
		xChart0.Legend(i) =  ds_chart0.GetColumn(i,"COMPS_NAME");	
		xChart0.Value(0, i) = ds_chart0.GetColumn(i,"SILQY");		
	}
	xChart0.CloseData("COD_Values");
	xChart0.Gallery = "Gallery_Pie";

	xChart0.AxisX.Gridlines = True;
	xChart0.AxisY.Gridlines = True;
    xChart0.AxisY.LabelsFormat.CustomFormat = "#,###"; // 출력될 라벨 포멧	
}

