function loadingChart2(){	
	xChart2.Titles[0].Text = "제품별 생산실적";

	var chartCnt = ds_chart1.rowCount();
	xChart2.OpenData("COD_Values", chartCnt, 12);

	//xChart0.SerLegBox = true;
	//xChart0.SerLeg(0) = "aaa";
	for(var i=0;i<chartCnt;i++){
		xChart2.SerLeg(i) = ds_chart1.GetColumn(i,"COMPS_NAME");
		xChart2.Value(i, 0) = ds_chart1.GetColumn(i,"SILQY_01");			
