function callback(svcId, rtnCd, rtnMsg){
	// tran 실패
	if(rtnCd<0){

		alert(setPopMessage("1002"));
		setMessage("1002");

	}else if(svcId=="getChartList"){
		// 조회건수가 없을경우
		if(ds_menuList.rowcount <= 0){	
			alert(setPopMessage("1003"));
			setMessage("1003");	
		// 정상 조회
		} else {
			loadingChart0();
			loadingChart1();
			loadingChart2();
			alert(setPopMessage("1004"));
			setMessage("1004");	
		}
	// tran 성공	
	}
}









