#include "script::lib_script_common.js";

var chartGubun;

function on_LoadCompleted(obj){
	loadingChart();		
}
function loadingChart(){		
	xChart0.OpenData("COD_Values", 1, 12);

	xChart0.Value(0, 0) = ds_chart.Sum("JPSAM_04");			
	xChart0.Value(0, 1) = ds_chart.Sum("JPSAM_05");			
	xChart0.Value(0, 2) = ds_chart.Sum("JPSAM_06");			
	xChart0.Value(0, 3) = ds_chart.Sum("JPSAM_07");			
	xChart0.Value(0, 4) = ds_chart.Sum("JPSAM_08");			
	xChart0.Value(0, 5) = ds_chart.Sum("JPSAM_09");			
	xChart0.Value(0, 6) = ds_chart.Sum("JPSAM_10");			
	xChart0.Value(0, 7) = ds_chart.Sum("JPSAM_11");			
	xChart0.Value(0, 8) = ds_chart.Sum("JPSAM_12");			
	xChart0.Value(0, 9) = ds_chart.Sum("JPSAM_01");			
	xChart0.Value(0, 10) = ds_chart.Sum("JPSAM_02");			
	xChart0.Value(0, 11) = ds_chart.Sum("JPSAM_03");							

	xChart0.CloseData("COD_Values");

	if(chartGubun==1){	//2D 막대
		xChart0.Gallery = "Gallery_Bar";
		xChart0.View3DDepth = 0;
	}else if(chartGubun==2){	//3D 막대
		xChart0.Gallery = "Gallery_Bar";
		xChart0.View3DDepth = 50;
	}else if(chartGubun==3){	//도넛
		xChart0.Gallery = "Gallery_Doughnut";
		xChart0.View3DDepth = 50;
	}

	xChart0.AxisX.Gridlines = True;
	xChart0.AxisY.Gridlines = True;

	xChart0.Legend(0) = "4월";
	xChart0.Legend(1) = "5월";
	xChart0.Legend(2) = "6월";
	xChart0.Legend(3) = "7월";
	xChart0.Legend(4) = "8월";
	xChart0.Legend(5) = "9월";
	xChart0.Legend(6) = "10월";
	xChart0.Legend(7) = "11월";
	xChart0.Legend(8) = "12월";
	xChart0.Legend(9) = "1월";
	xChart0.Legend(10) = "2월";
	xChart0.Legend(11) = "3월";

	xChart0.AxisY.LabelsFormat.CustomFormat = "#,###"; // 출력될 라벨 포멧	
}
