#include "script::lib_script_common.js";

var Lib = G_ds_user.GetColumn(0,"LIB2");
var nowDate = today();
var procs = "" ;
var ordat = "" ;

/********************************************************************** 
 * 개 발 자 : 황규철 
 * 개발일자 : 2010-11-26
 * 내용설명 : 화면로드
 * 주요변수 : 
 **********************************************************************/ 
function on_LoadCompleted(obj){
	getTitle();	
	getComCode();
	ed_syyyymmdd.Value = nowDate;	
}


/********************************************************************** 
 * 개 발 자 : 황규철 
 * 개발일자 : 2010-11-26
 * 내용설명 : 공통코드 조회
 * 주요변수 : 
 **********************************************************************/ 
function getComCode(){

	http.Sync 	= true;

	var sKind		= "getComCode";						
	var sMethodName = "service::login/selectComCode.do"; 
	var sInDataSet  = "";
	var sOutDataSet = "ds_ComCode_PLNT=ds_ComCode_PLNT";
	var sArgument   = "";
		sArgument   += " CLASS='PLNT'";
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");

	ds_ComCode_PLNT.Filter("code2=='" + G_ds_user.GetColumn(0,"PLANT_CD") +"'");


	http.Sync 	= true;
	var sKind		= "getComCode2";						
	var sMethodName = "service::sample/selectPpmi001MCode.do"; 	
	var sInDataSet  = "";
	var sOutDataSet = "ds_ppmi001mCode=ds_ppmi001mCode";
	var sArgument   = "";
		sArgument   += " LIB=" + quote(Lib);
		sArgument   += " COMPS=" + quote(G_ds_user.GetColumn(0,"PLANT_CD"));

	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");

	ds_ppmi001mCode.InsertRow(0);
	ds_ppmi001mCode.SetColumn(0,"ROWGB","");
	ds_ppmi001mCode.SetColumn(0,"ROWNM","[전체]");
	Combo0.index = 0;	

}



/********************************************************************** 
 * 개 발 자 : 황규철 
 * 개발일자 : 2010-11-26
 * 내용설명 : 화면조회
 * 주요변수 : 
 **********************************************************************/ 
function searchCode(obj)
{
	var arrReturn = dialog("Common::comFgp260pf.xml","" ,300,300);

	for(i=0;i<length(arrReturn);i++)
	{
		var codes = split(arrReturn[i],"|");
		// *
		object("ed_code" + (i+1)).Column 	= codes[0];
		object("ed_code" + (i+1)).value 	= codes[1];		
	}   
}



/********************************************************************** 
 * 개 발 자 : 황규철 
 * 개발일자 : 2010-11-26
 * 내용설명 : 화면조회
 * 주요변수 : 
 **********************************************************************/ 
function getPpmi006mTab2(){
	http.Sync = true;
	var sKind		= "getPpmi006mTab2";						
	var sMethodName = "service::sample/selectPpmi006MTab2.do";     					// 서버에서 호출 될 Method 명
	var sInDataSet  = "";                             								// 자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet = "ds_ppmi006mTab2=ds_ppmi006mTab2";    				// 조회 후 돌려 받을 DataSet 명칭
	var sArgument   = "";

	sArgument += " LIB=" + quote(Lib);
	sArgument += " COMPS=" + quote(G_ds_user.GetColumn(0,"PLANT_CD"));
	sArgument += " PROCS=" + quote(procs);	
	sArgument += " ORDAT=" + quote(ordat);
	sArgument += " DAYGU=" + quote(cd_daygu.Value);	
	sArgument += " BADLG=" + quote(trim(ed_code1.Column));	
	sArgument += " BADMD=" + quote(trim(ed_code2.Column));	
	sArgument += " BADSM=" + quote(trim(ed_code3.Column));	
	sArgument += " PSPNO=" + quote(ed_procs.Value);	
	sArgument += " BADQY=" + quote(Edit2.Value);	
	sArgument += " BADTM=" + quote(MaskEdit0.Value);	
	sArgument += " BADNM=" + quote(Edit0.Value);				

	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");

}




/********************************************************************** 
 * 개 발 자 : 황규철 
 * 개발일자 : 2010-12-08
 * 내용설명 : 화면저장
 * 주요변수 : 
 **********************************************************************/ 
function setPpmi006mTab2(){
	if(!validationChk()) return;
	var sKind		= "setPpmi006mTab2";						
	var sMethodName = "service::sample/savePpmi006MTab2.do";     		// 서버에서 호출 될 Method 명
	var sInDataSet  = "ds_ppmi006mTab2=ds_ppmi006mTab2:U";     			// 자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet = "";    											// 조회 후 돌려 받을 DataSet 명칭
	var sArgument   = "";

	sArgument += " LIB=" + quote(Lib);
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "callback");

}



/********************************************************************** 
 * 개 발 자 : 황규철 
 * 개발일자 : 2010-11-26
 * 내용설명 : validation 체크
 * 주요변수 : 
 **********************************************************************/ 
function validationChk(){
	for(var i=0; i<ds_ppmi006mTab2.rowcount(); i++){

		if(length(trim(ds_ppmi006mTab2.GetColumn(i, "DAYGU")))==0){
			alert(setPopMessage("2027"));	// 20110103l-koowgnojeel, 2027 == 주야구분을 선택하세요.
			ds_ppmi006mTab2.row = i;
			return false;
		}

		if(length(trim(ds_ppmi006mTab2.GetColumn(i, "BADTM")))==0){
			alert(setPopMessage("2051"));	// 20110103l-koowgnojeel, 2051 == 발행시간을 입력하세요.
			ds_ppmi006mTab2.row = i;
			return false;
		}

		if(length(trim(ds_ppmi006mTab2.GetColumn(i, "BADLG")))==0){
			alert(setPopMessage("2052"));	// 20110103l-koowgnojeel, 2052 == 코드를 선택하세요.
			ds_ppmi006mTab2.row = i;
			return false;
		}

		if(length(trim(ds_ppmi006mTab2.GetColumn(i, "PSPNO")))==0){
			alert(setPopMessage("2017"));	// 20110103l-koowgnojeel, 2017 == 품번을 입력하세요.
			ds_ppmi006mTab2.row = i;
			return false;
		}
		if(length(trim(ds_ppmi006mTab2.GetColumn(i, "BADQY")))==0){
			alert(setPopMessage("2023"));	// 20110103l-koowgnojeel, 2023 == 불량수를 입력하세요.
			ds_ppmi006mTab2.row = i;
			return false;
		}			
		if(length(trim(ds_ppmi006mTab2.GetColumn(i, "BADNM")))==0){
			alert(setPopMessage("2024"));	// 20110103l-koowgnojeel, 2024 == 발생사유를 입력하세요.
			ds_ppmi006mTab2.row = i;
			return false;
		}			
	}

	return true;
}




function onRowPosChange(obj,nOldRow,nRow)
{
	if(ds_ppmi006mTab1.GetRowType(ds_ppmi006mTab1.row) == "insert"){
		cd_daygu.Enable = true;
		Edit2.Enable = true;
		Edit1.Enable = true;	
	} else {
		cd_daygu.Enable = false;
		Edit2.Enable = false;
		Edit1.Enable = false;	
	}

}

