 * 개 발 자 : 황규철 
 * 개발일자 : 2010-12-06
 * 내용설명 : 엑셀파일로 저장
 * 주요변수 : 
 **********************************************************************/ 
function deleteCode(obj)
{
	ed_code1.Value = "";
	ed_code2.Value = "";
	ed_code3.Value = "";

	ed_code1.Column = "";
	ed_code2.Column = "";
	ed_code3.Column = "";

}


function onCellClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{
	if(nCell == 6){
		var arrReturn = dialog("Common::comFgp260pf.xml","" ,300,300);

		for(i=0;i<length(arrReturn);i++)
		{
			var codes = split(arrReturn[i],"|");

			if(i ==0)
			{
				ds_ppmi006mTab2.SetColumn(ds_ppmi006mTab2.row,"BADLG", trim(codes[0]));
				ds_ppmi006mTab2.SetColumn(ds_ppmi006mTab2.row,"BADLG_NAME", codes[1]);
			}

			if(i ==1)
			{
				ds_ppmi006mTab2.SetColumn(ds_ppmi006mTab2.row,"BADMD", trim(codes[0]));
				ds_ppmi006mTab2.SetColumn(ds_ppmi006mTab2.row,"BADMD_NAME", codes[1]);
			}

			if(i ==2)
			{
				ds_ppmi006mTab2.SetColumn(ds_ppmi006mTab2.row,"BADSM", trim(codes[0]));
				ds_ppmi006mTab2.SetColumn(ds_ppmi006mTab2.row,"BADSM_NAME", codes[1]);
			}						
		}   

	}
}



/********************************************************************** 
 * 개 발 자 : 황규철 
 * 개발일자 : 2010-11-26
 * 내용설명 : Transaction 리턴후 실행 함수
 * 주요변수 : 
 * 			- sMethodName	Transaction 고유 ID
 * 			- ErrorCode 	에러코드
 * 			- ErrorMSG 		에러 메세지
 **********************************************************************/ 
function callback(svcId, rtnCd, rtnMsg){
	// tran 실패
	if(rtnCd<0){
		alert(setPopMessage("1002"));
		setMessage("1002");

	}else if(svcId=="getPpmi006mTab2"){
		// 조회건수가 없을경우
		if(ds_ppmi006mTab2.rowcount <= 0){
			alert(setPopMessage("1003"));
			setMessage("1003");	
		// 정상 조회
		} else {
			alert(setPopMessage("1004"));
			setMessage("1004");				
		}	
	}else if(svcId=="setPpmi006mTab2"){
		alert(setPopMessage("1001"));
		setMessage("1001");
	}	

}

