function fn_Input()
{
	ds_ppmi006mTab2.AddColumn("COMPS","STRING",255);
	ds_ppmi006mTab2.AddColumn("ORDAT","STRING",255);
	ds_ppmi006mTab2.AddColumn("PROCS","STRING",255);
	ds_ppmi006mTab2.AddColumn("DAYGU","STRING",255);
	ds_ppmi006mTab2.AddColumn("BADTM","STRING",255);

	ds_ppmi006mTab2.AddColumn("BADLG_NAME","STRING",255);
	ds_ppmi006mTab2.AddColumn("BADMD_NAME","STRING",255);
	ds_ppmi006mTab2.AddColumn("BADSM_NAME","STRING",255);	

	ds_ppmi006mTab2.AddColumn("BADLG","STRING",255);
	ds_ppmi006mTab2.AddColumn("BADMD","STRING",255);
	ds_ppmi006mTab2.AddColumn("BADSM","STRING",255);
	ds_ppmi006mTab2.AddColumn("BADNM","STRING",255);
	ds_ppmi006mTab2.AddColumn("PSPNO","STRING",255);
	ds_ppmi006mTab2.AddColumn("BADQY","STRING",255);


	if(checkInsertCount("ds_ppmi006mTab2")== false) return;	

	ds_ppmi006mTab2.AddRow();

	ds_ppmi006mTab2.SetColumn(ds_ppmi006mTab2.row,"COMPS",G_ds_user.GetColumn(0,"PLANT_CD"));
	ds_ppmi006mTab2.SetColumn(ds_ppmi006mTab2.row,"ORDAT",ordat);
	ds_ppmi006mTab2.SetColumn(ds_ppmi006mTab2.row,"PROCS",procs);


}

