function on_LoadCompleted(obj){
	getTitle();	
	getComCode();

}


/********************************************************************** 
 * 개 발 자 : 황규철 
 * 개발일자 : 2010-11-26
 * 내용설명 : 공통코드 조회
 * 주요변수 : 
 **********************************************************************/ 
function getComCode(){

	http.Sync 	= true;

	var sKind		= "getComCode";						
	var sMethodName = "service::login/selectComCode.do"; 
	var sInDataSet  = "";
	var sOutDataSet = "ds_ComCode_PLNT=ds_ComCode_PLNT";
	var sArgument   = "";
		sArgument   += " CLASS='PLNT'";
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");

	ds_VComCode_PLNT.Copy("ds_ComCode_PLNT");

	ds_ComCode_PLNT.Filter("code2=='" + G_ds_user.GetColumn(0,"PLANT_CD") +"'");


	http.Sync 	= true;
	var sKind		= "getComCode2";						
	var sMethodName = "service::sample/selectPpmi001MCode.do"; 	
	var sInDataSet  = "";
	var sOutDataSet = "ds_ppmi001mCode=ds_ppmi001mCode";
	var sArgument   = "";
		sArgument   += " LIB=" + quote(Lib);
		sArgument   += " COMPS=" + quote(G_ds_user.GetColumn(0,"PLANT_CD"));

	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");

	ds_vppmi001mCode.Copy("ds_ppmi001mCode");

	ds_ppmi001mCode.InsertRow(0);
	ds_ppmi001mCode.SetColumn(0,"ROWGB","");
	ds_ppmi001mCode.SetColumn(0,"ROWNM","[전체]");
	Combo0.index = 0;	


/*
	ds_ComCode_PLNT.InsertRow(0);
	ds_ComCode_PLNT.SetColumn(0,"CODE1","");
	ds_ComCode_PLNT.SetColumn(0,"CODE_NAME","[전체]");
	cb_comp.index = 0;	
*/	
}


/********************************************************************** 
 * 개 발 자 : 황규철 
 * 개발일자 : 2010-11-26
 * 내용설명 : 그룹 조회
 * 주요변수 : 
 **********************************************************************/ 
function getPpmi003mGroup(){
	var sKind		= "getPpmi003mGroup";						
	var sMethodName = "service::sample/selectPpmi003MGroup.do";     		// 서버에서 호출 될 Method 명
	var sInDataSet  = "";                             						// 자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet = "ds_ppmi003mGroup=ds_ppmi003mGroup";    				// 조회 후 돌려 받을 DataSet 명칭
	var sArgument   = "";

	sArgument += " LIB=" + quote(Lib);
	sArgument += " COMPS=" + quote(cb_comp.Value);
	sArgument += " PROCS=" + quote(ed_procs.Value);
	sArgument += " PRSNM=" + quote(ed_procsName.Value);
	sArgument += " ROWGB=" + quote(Combo0.Value);	

	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");

}


/********************************************************************** 
 * 개 발 자 : 황규철 
 * 개발일자 : 2010-11-26
 * 내용설명 : 그룹 데이터 변경 이벤트
 * 주요변수 : 
 **********************************************************************/ 
function OnRowPosChanged(obj,nOldRow,nRow)
{
	var sKind		= "getPpmi003m";						
	var sMethodName = "service::sample/selectPpmi003M.do";     		// 서버에서 호출 될 Method 명
	var sInDataSet  = "";                             				// 자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet = "ds_ppmi003m=ds_ppmi003m";    				// 조회 후 돌려 받을 DataSet 명칭
	var sArgument   = "";

	sArgument += " LIB=" + quote(Lib);
	sArgument += " COMPS=" + quote(ds_ppmi003mGroup.GetColumn(ds_ppmi003mGroup.row,"COMPS"));
	sArgument += " PROCS=" + quote(ds_ppmi003mGroup.GetColumn(ds_ppmi003mGroup.row,"PROCS"));
	sArgument += " PRSNM=" + quote(ds_ppmi003mGroup.GetColumn(ds_ppmi003mGroup.row,"PRSNM"));

	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");	
}

/********************************************************************** 
 * 개 발 자 : 황규철 
 * 개발일자 : 2010-11-26
 * 내용설명 : 화면 저장
 * 주요변수 : 
 **********************************************************************/ 
function setPpmi009m()
{
	if(!validationChk()) return;
	var sKind		= "setPpmi009m";						
	var sMethodName = "service::sample/savePpmi009M.do";     // 서버에서 호출 될 Method 명
	var sInDataSet  = "ds_ppmi003m=ds_ppmi003m:U";     		//자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet = "";    								// 조회 후 돌려 받을 DataSet 명칭
	var sArgument   = "";

	sArgument += " LIB=" + quote(Lib);
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "callback");
}

/********************************************************************** 
 * 개 발 자 : 황규철 
 * 개발일자 : 2010-11-26
 * 내용설명 : validation 체크
 * 주요변수 : 
 **********************************************************************/ 
function validationChk(){
	for(var i=0; i<ds_ppmi003m.rowcount(); i++){

		if(length(trim(ds_ppmi003m.GetColumn(i, "PROCS")))==0){
			alert(setPopMessage("2026"));	// 20110103l-koowgnojeel, 2026 == 공정코드를 입력하세요.
			ds_ppmi003m.row = i;
			return false;
		}

		if(length(trim(ds_ppmi003m.GetColumn(i, "ROWGB")))==0){
			alert(setPopMessage("2028"));	// 20110103l-koowgnojeel, 2028 == ROW 구분을 입력해 주세요.
			ds_ppmi003m.row = i;
			return false;
		}
		if(length(trim(ds_ppmi003m.GetColumn(i, "SEQNO")))==0){
			alert(setPopMessage("2029"));	// 20110103l-koowgnojeel, 2029 == 정렬 순서를 입력해 주세요.
			ds_ppmi003m.row = i;
			return false;
		}

		if(length(trim(ds_ppmi003m.GetColumn(i, "PRSNM")))==0){
			alert(setPopMessage("2030"));	// 20110103l-koowgnojeel, 2030 == 공정명을 입력해 주세요.
			ds_ppmi003m.row = i;
			return false;
		}
		if(length(trim(ds_ppmi003m.GetColumn(i, "GIJUN")))==0){
			alert(setPopMessage("2053"));	// 20110103l-koowgnojeel, 2053 == 기준텍트타임을 입력하세요.
			ds_ppmi003m.row = i;
			return false;
		}

		if(length(trim(ds_ppmi003m.GetColumn(i, "CRPRD")))==0){
			alert(setPopMessage("2032"));	// 20110103l-koowgnojeel, 2032 == 대표생산품을 입력해 주세요.
			ds_ppmi003m.row = i;
			return false;
		}				

	}
	return true;
}


/********************************************************************** 
 * 개 발 자 : 황규철 
 * 개발일자 : 2010-11-26
 * 내용설명 : Transaction 리턴후 실행 함수
 * 주요변수 : 
 * 			- sMethodName	Transaction 고유 ID
 * 			- ErrorCode 	에러코드
 * 			- ErrorMSG 		에러 메세지
 **********************************************************************/ 
function callback(svcId, rtnCd, rtnMsg){
	// tran 실패
	if(rtnCd<0){
		alert(setPopMessage("1002"));
		setMessage("1002");

	}else if(svcId=="getPpmi003m"){
		// 조회건수가 없을경우
		if(ds_ppmi003m.rowcount <= 0){
			alert(setPopMessage("1003"));
			setMessage("1003");	
		// 정상 조회
		} else {
			alert(setPopMessage("1004"));
			setMessage("1004");				
		}
	// tran 성공	
	}else if(svcId=="setPpmi009m"){
		alert(setPopMessage("1001"));
		setMessage("1001");
		getPpmi003mGroup();
	}	
}


