Window.Script:
function BHZZ100SM_OnLoadCompleted(obj)
{
	Ubi_mediReport.setJrfFileDir(G_baseURL+"/ui/Group2/report/");
	Ubi_mediReport.setJrfFileName("rep3.jrf");

	ds_report_temp.Copy(ds_report);	
	var currow = ds_report.currow;
	ds_report_temp.Filter("currow="+currow);

	ds_report_currow.CopyF(ds_report_temp);	
	Ubi_mediReport.SetDataset("ds_report", "ds_report_currow");	

	var comps = ds_report_currow.GetColumn(0, "COMPS");
	ds_provider.Filter("COMPS='"+COMPS+"'");	

	Ubi_mediReport.SetArgument("VENDERNO", ds_provider.GetColumn(0, "VENDERNO"));
	Ubi_mediReport.SetArgument("COMPANY", ds_provider.GetColumn(0, "COMPANY"));
	Ubi_mediReport.SetArgument("UPTAE", ds_provider.GetColumn(0, "UPTAE"));
	Ubi_mediReport.SetArgument("UPJONG", ds_provider.GetColumn(0, "UPJONG"));
	Ubi_mediReport.SetArgument("CEONAME", ds_provider.GetColumn(0, "CEONAME"));
	Ubi_mediReport.SetArgument("ADDRESS", ds_provider.GetColumn(0, "ADDRESS"));

	Ubi_mediReport.Retrieve();
}
