function fnc_OnKeyDown(obj,nChar,bShift,bCtrl, bAlt,LLParam,HLParam){
	if(nChar == 13){
		sendColumn();
		sendField();
	}
}

function btn_add_OnClick(obj)
{
	sendColumn();
	MAX5250.SendKeys("[Enter]");
	SetTimer(1,100);

	//close(1);
}


//page down
function btn_pgdn_OnClick(obj)
{	
	page += 1;
	//chkPage(page);
	MAX5250.SendKeys("[PDN]");
	chkPagePop(page,22,67);
}

function btn_pgup_OnClick(obj)
{
	page -= 1;
	///chkPage(page);
	MAX5250.SendKeys("[PUP]");
	chkPagePop(page,22,67);
}

function form_OnTimer(obj,nEventID)
{
	if(nEventID == 1){
		popClose();
		killTimer(1);
	}
}
function popClose(){
	close(1);
}
