function sendColumn(){
	Max5250.SendToColumn("COLUMN_01",ds_column.getColumn(0,"COLUMN_01"),1);
	Max5250.SendToColumn("COLUMN_02",ds_column.getColumn(0,"COLUMN_02"),1);
	Max5250.SendToColumn("COLUMN_03",ds_column.getColumn(0,"COLUMN_03"),1);
	Max5250.SendToColumn("COLUMN_04",ds_column.getColumn(0,"COLUMN_04"),1);
	Max5250.SendToColumn("COLUMN_05",ds_column.getColumn(0,"COLUMN_05"),1);
	Max5250.SendToColumn("COLUMN_06",ds_column.getColumn(0,"COLUMN_06"),1);
}
function sendField(){
	MAX5250.SendToField("FD01013",FD01013.Text );
	MAX5250.SendToField("FD03014",FD03014.Text );
	MAX5250.SendKeys("[Enter]");
}
function form_OnLoadCompleted(obj)
{
	form.SetFocus();
	var subStr = "";
	var sKind		= "CONVERTXMLDATA";	
	var sMethodName = "service::max5250/convertxmlData.do"; 
	var sInDataSet  = ""; 	
	var sOutDataSet = "ds_main=ds_main"; 	
	var mainStr = max5250.GetScreenData(1,24); 	
	var arg			= " str=" + quote(mainStr); 	
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, arg, "fnc_callBack"); 	
}
