function sendField(){
	MAX5250.SendToField("FD12040",FD12040.Text );
	MAX5250.SendToField("FD12063",FD12063.Text );
	MAX5250.SendToField("FD13040",FD13040.Text );
	MAX5250.SendToField("FD13063",FD13063.Text );
