#include "script::lib_script_common.js";
function sendColumn(){
	Max5250.SendToColumn("COLUMN_01",ds_column.getColumn(0,"COLUMN_01"),1);
	Max5250.SendToColumn("COLUMN_02",ds_column.getColumn(0,"COLUMN_02"),1);
	Max5250.SendToColumn("COLUMN_03",ds_column.getColumn(0,"COLUMN_03"),1);
	Max5250.SendToColumn("COLUMN_04",ds_column.getColumn(0,"COLUMN_04"),1);
	Max5250.SendToColumn("COLUMN_05",ds_column.getColumn(0,"COLUMN_05"),1);
	Max5250.SendToColumn("COLUMN_06",ds_column.getColumn(0,"COLUMN_06"),1);
	Max5250.SendToColumn("COLUMN_07",ds_column.getColumn(0,"COLUMN_07"),1);
}
function sendField(){
	MAX5250.SendToField("FD02011",FD02011.Text );
	MAX5250.SendToField("FD02031",FD02031.Text );
	MAX5250.SendToField("FD03011",FD03011.Text );
	MAX5250.SendToField("FD03031",FD03031.Text );
	MAX5250.SendToField("FD03079",FD03079.Text );
	MAX5250.SendKeys("[Enter]");
}
function form_OnLoadCompleted(obj)
{
	MainGrid.SetCellProp("head",0,"text",ds_head.getColumn(0,"COLUMN_01"));
	MainGrid.SetCellProp("head",1,"text",ds_head.getColumn(0,"COLUMN_02"));
	MainGrid.SetCellProp("head",2,"text",ds_head.getColumn(0,"COLUMN_03"));
	MainGrid.SetCellProp("head",3,"text",ds_head.getColumn(0,"COLUMN_04"));
	MainGrid.SetCellProp("head",4,"text",ds_head.getColumn(0,"COLUMN_05"));
	MainGrid.SetCellProp("head",5,"text",ds_head.getColumn(0,"COLUMN_06"));
	MainGrid.SetCellProp("head",6,"text",ds_head.getColumn(0,"COLUMN_07"));
	MainGrid.SetCellProp("head",7,"text",ds_head.getColumn(0,"COLUMN_08"));
	form.SetFocus();
	if(IsValid(btn_pgdn))
		chkPage(page); //page check
	var subStr = "";
	var sKind		= "CONVERTXMLDATA";	
	var sMethodName = "service::max5250/convertxmlData.do"; 
	var sInDataSet  = ""; 	
	var sOutDataSet = "ds_main=ds_main"; 	
	var mainStr = max5250.GetScreenData(1,24); 	
	var arg			= " str=" + quote(mainStr); 	
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, arg, "fnc_callBack"); 	
}
