 * 내용설명 : 사업부 선택
 * 주요변수 : 
 **********************************************************************/ 
function getComCode(){
	/*
	http.Sync 	= true;

	var sKind		= "getComCode";						
	var sMethodName = "service::login/selectComCode.do"; 
	var sInDataSet  = "";
	var sOutDataSet = "ds_ComCode_PLNT=ds_ComCode_PLNT";
	var sArgument   = "";
		sArgument   += " CLASS='PLNT'";
		sArgument   += " CODE1=" + quote(company);		
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	cb_searchCodeName1.Value = comps;
	*/
	setCompanyName(ds_ComCode_GROUP);
	cb_searchCodeName1.Value=company;
	if (comps != "E1"){
		http.Sync 	= true;
		var sKind		= "getMST050";						
		var sMethodName = "service::pscomlib/selectMST050.do"; 	
		var sInDataSet  = "";
		var sOutDataSet = "ds_MST050PF=ds_MST050PF";
		var sArgument   = "";
			sArgument   += " LIB2=" + quote(Lib2);
			sArgument   += " COMPS=" + quote(comps);
			sArgument   += " GRPCD='PRD'";
			sArgument   += " MSTGU='$'";
		Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
		http.Sync 	= false;
		var row = ds_MST050PF.rowcount();
		ds_MST050PF.InsertRow(row);
		ds_MST050PF.SetColumn(row,"mstcd","");
		ds_MST050PF.SetColumn(row,"mstrm","[전체]");
		cb_itcls1.index = 0;
	}
}

/********************************************************************** 
 * 내용설명 : 화면조회
 * 주요변수 : 
 **********************************************************************/ 
function getCJJZ301Q(){

	var selectFile;
	if (cb_itcls1.Value==""){
		alert("전체선택시 많은 시간이 소요됩니다. IT전략팀으로 연락바랍니다. ");
		return;
	}

	if (cb_searchCodeName1.Value=="PE"){
		if(confirm("많은 시간이 소요됩니다.  작업하시겠습니까? ") == false) return;
	}

	if (cmbGubun == "A"){
		selectFile ="F#D30PR";
		date_D30DT ="";
	}else{
		selectFile = "F#D30PRALL";
		if(length(trim(ca_YYYY.Value)) < 8){
			alert(setPopMessage("4674"));
			setMessage("4674");
			ca_YYYY.SetFocus();
			return;
		}
		if(cmbSelect.Value = "1"){     //상반기
			date_D30DT = substr(ca_YYYY.Value,0,4) + "0331";
		}else{                         //하반기
			date_D30DT = substr(ca_YYYY.Value,0,4) + "0930";
		}

		if (cb_searchCodeName1.Value=="PS" && toNumber(date_D30DT) < 20050930){
			alert("2005년 하반기 이전 Data는 존재하지 않습니다. 확인바랍니다.");
			return;
		}
		if (cb_searchCodeName1.Value=="PE" && toNumber(date_D30DT) < 20090930){
			alert("2009년 하반기 이전 Data는 존재하지 않습니다. 확인바랍니다.");
			return;
		}
	}

	var step;
	for(step=1 ;step<26;step++){
		var start_rownumber=0;
		var end_rownumber=0;
		switch (step){
		case 1 : 
			start_rownumber = 1;	end_rownumber=10000;	break;
		case 2 : 
			start_rownumber = 10001;	end_rownumber=20000;	break;
		case 3 : 
			start_rownumber = 20001;	end_rownumber=30000;	break;
		case 4 : 
			start_rownumber = 30001;	end_rownumber=40000;	break;
		case 5 : 
			start_rownumber = 40001;	end_rownumber=50000;	break;
		case 6 : 
			start_rownumber = 50001;	end_rownumber=60000;	break;
		case 7 : 
			start_rownumber = 60001;	end_rownumber=70000;	break;
		case 8 : 
			start_rownumber = 70001;	end_rownumber=80000;	break;
		case 9 : 
			start_rownumber = 80001;	end_rownumber=90000;	break;
		case 10 : 
			start_rownumber = 90001;	end_rownumber=100000;	break;
		case 11 : 
			start_rownumber = 100001;	end_rownumber=110000;	break;
		case 12 : 
			start_rownumber = 110001;	end_rownumber=120000;	break;
		case 13 : 
			start_rownumber = 120001;	end_rownumber=130000;	break;
		case 14 : 
			start_rownumber = 130001;	end_rownumber=140000;	break;
		case 15 : 
			start_rownumber = 140001;	end_rownumber=150000;	break;
		case 16 : 
			start_rownumber = 150001;	end_rownumber=160000;	break;
		case 17 : 
			start_rownumber = 160001;	end_rownumber=170000;	break;
		case 18 : 
			start_rownumber = 170001;	end_rownumber=180000;	break;
		case 19 : 
			start_rownumber = 180001;	end_rownumber=190000;	break;
		case 20 : 
			start_rownumber = 190001;	end_rownumber=200000;	break;
		case 21 : 
			start_rownumber = 200001;	end_rownumber=210000;	break;
		case 22 : 
			start_rownumber = 210001;	end_rownumber=220000;	break;
		case 23 : 
			start_rownumber = 220001;	end_rownumber=230000;	break;
		case 24 : 
			start_rownumber = 230001;	end_rownumber=240000;	break;
		case 25 : 
			start_rownumber = 240001;	end_rownumber=250000;	break;
		}
		var sKind		= "getCJJZ301Q_"+step;						
		var sMethodName = "service::pscstlib/selectCJJZ301Q.do";     		// 서버에서 호출 될 Method 명
		var sInDataSet  = "";                             				// 자료 저장 시 보낼 DataSet 명칭
		var sOutDataSet = "ds_CJJZ301Q_"+step+"=ds_CJJZ301Q_"+step;    				// 조회 후 돌려 받을 DataSet 명칭
		var sArgument   = "";
		sArgument += " LIB1=" + quote(Lib1);
		sArgument += " LIB2=" + quote(Lib2);
		sArgument += " STEP=" + quote(step);
		sArgument += " F_ROWNUM=" + quote(start_rownumber);
		sArgument += " T_ROWNUM=" + quote(end_rownumber);
		sArgument += " D30PF=" + quote(selectFile); 
		sArgument += " D30DT=" + quote(date_D30DT);
		sArgument += " GUB=" + quote(cmbGubun.Value);
		sArgument += " ITCLS1=" + quote(cb_itcls1.Value);

		http.Sync = true;
		Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "callback");
		http.Sync = false;
		if(object("ds_CJJZ301Q_"+step).rowcount != 10000){
			step=26;
		}
	}
	if (ds_CJJZ301Q_1.rowcount > 0) ds_CJJZ301Q.AppendDataset("ds_CJJZ301Q_1");
	if (ds_CJJZ301Q_2.rowcount > 0) ds_CJJZ301Q.AppendDataset("ds_CJJZ301Q_2");
	if (ds_CJJZ301Q_3.rowcount > 0) ds_CJJZ301Q.AppendDataset("ds_CJJZ301Q_3");
	if (ds_CJJZ301Q_4.rowcount > 0) ds_CJJZ301Q.AppendDataset("ds_CJJZ301Q_4");
	if (ds_CJJZ301Q_5.rowcount > 0) ds_CJJZ301Q.AppendDataset("ds_CJJZ301Q_5");
	if (ds_CJJZ301Q_6.rowcount > 0) ds_CJJZ301Q.AppendDataset("ds_CJJZ301Q_6");
	if (ds_CJJZ301Q_7.rowcount > 0) ds_CJJZ301Q.AppendDataset("ds_CJJZ301Q_7");
	if (ds_CJJZ301Q_8.rowcount > 0) ds_CJJZ301Q.AppendDataset("ds_CJJZ301Q_8");
	if (ds_CJJZ301Q_9.rowcount > 0) ds_CJJZ301Q.AppendDataset("ds_CJJZ301Q_9");
	if (ds_CJJZ301Q_10.rowcount > 0) ds_CJJZ301Q.AppendDataset("ds_CJJZ301Q_10");
	if (ds_CJJZ301Q_11.rowcount > 0) ds_CJJZ301Q.AppendDataset("ds_CJJZ301Q_11");
	if (ds_CJJZ301Q_12.rowcount > 0) ds_CJJZ301Q.AppendDataset("ds_CJJZ301Q_12");
	if (ds_CJJZ301Q_13.rowcount > 0) ds_CJJZ301Q.AppendDataset("ds_CJJZ301Q_13");
	if (ds_CJJZ301Q_14.rowcount > 0) ds_CJJZ301Q.AppendDataset("ds_CJJZ301Q_14");
	if (ds_CJJZ301Q_15.rowcount > 0) ds_CJJZ301Q.AppendDataset("ds_CJJZ301Q_15");
	if (ds_CJJZ301Q_16.rowcount > 0) ds_CJJZ301Q.AppendDataset("ds_CJJZ301Q_16");
	if (ds_CJJZ301Q_17.rowcount > 0) ds_CJJZ301Q.AppendDataset("ds_CJJZ301Q_17");
	if (ds_CJJZ301Q_18.rowcount > 0) ds_CJJZ301Q.AppendDataset("ds_CJJZ301Q_18");
	if (ds_CJJZ301Q_19.rowcount > 0) ds_CJJZ301Q.AppendDataset("ds_CJJZ301Q_19");
	if (ds_CJJZ301Q_20.rowcount > 0) ds_CJJZ301Q.AppendDataset("ds_CJJZ301Q_20");
	if (ds_CJJZ301Q_21.rowcount > 0) ds_CJJZ301Q.AppendDataset("ds_CJJZ301Q_21");
	if (ds_CJJZ301Q_22.rowcount > 0) ds_CJJZ301Q.AppendDataset("ds_CJJZ301Q_22");
	if (ds_CJJZ301Q_23.rowcount > 0) ds_CJJZ301Q.AppendDataset("ds_CJJZ301Q_23");
	if (ds_CJJZ301Q_24.rowcount > 0) ds_CJJZ301Q.AppendDataset("ds_CJJZ301Q_24");
	if (ds_CJJZ301Q_25.rowcount > 0) ds_CJJZ301Q.AppendDataset("ds_CJJZ301Q_25");
}


/********************************************************************** 
 * 내용설명 : 엑셀파일로 저장
 * 주요변수 : 
 **********************************************************************/ 
function setExcel()
{
	var fName   = "Standard Cost_"+date_D30DT;
	var arrSheets  =  Array();  

	arrSheets[0]  = "MainGrid|Standard Cost_"+date_D30DT;
	createExcelSheetS2(FileDialog0, fName ,arrSheets);

}


/********************************************************************** 
 * 내용설명 : Transaction 리턴후 실행 함수
 * 주요변수 : 
 * 			- sMethodName	Transaction 고유 ID
 * 			- ErrorCode 	에러코드
 * 			- ErrorMSG 		에러 메세지
 **********************************************************************/ 
function callback(svcId, rtnCd, rtnMsg){
	// tran 실패
	if(rtnCd<0){
		alert(setPopMessage("1002"));
		setMessage("1002");

	}else if(svcId=="getBZZE401Q"){
		// 조회건수가 없을경우
		if(ds_BZZE401Q.rowcount <= 0){
			alert(setPopMessage("1003"));
			setMessage("1003");	
		// 정상 조회
		} else {
			alert(setPopMessage("1004"));
			setMessage("1004");				
		}
	}
}

function Grid0_OnHeadClick(obj,nCell,nX,nY,nPivotIndex)
{
