function cmbGubun_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	if(strCode=="A"){
		Static3.Visible = false;
		Static0.Visible = false;
		ca_YYYY.Visible = false;
		Static5.Visible = false;
		Static6.Visible = false;
		cmbSelect.Visible = false;
	}
	if(strCode=="B"){
		Static3.Visible = true;
		Static0.Visible = true;
		ca_YYYY.Visible = true;
		Static5.Visible = true;
		Static6.Visible = true;
		cmbSelect.Visible = true;
	}
	common_OnChanged();
}
