function setExcel()
{
	var fName   = "박스코드정보마스타";
	var arrSheets  =  Array();  

	arrSheets[0]  = "MainGrid|박스코드정보마스타";
	createExcelSheetS2(FileDialog0, fName ,arrSheets);

}

