function fn_Search()
{
	getFACC110();
}

function fn_Input()
{
	if(checkInsertCount("ds_FACC110")== false) return;
	if(ds_FACC110.rowcount < 1) fn_Search();  
	ds_FACC110.AddRow();
	ds_FACC110.SetColumn(ds_FACC110.currow,"BXCOMP",ComboBoxPlant.Value);
	ds_FACC110.SetColumn(ds_FACC110.currow,"BXADID",userId);
}

function fn_Delete()
{
	if(ds_FACC110.rowcount < 1) return;			//삭제가 없으면 건너뛰기		
	ds_FACC110.DeleteRow(ds_FACC110.currow);	//삭제		
	alert(setPopMessage("1011"));				//[알림] 화면에서만 제거되었습니다. 저장하시면 삭제됩니다.		
	setMessage("1011");
}

