function ds_FACC110_CanRowPosChange(obj,nOldRow,nRow)
{
	var result=true;
	if(ds_FACC110.GetRowType(ds_FACC110.currow)=="update" ||ds_FACC110.GetRowType(ds_FACC110.currow)=="insert" ){
		var bxcd=ds_FACC110.GetColumn(ds_FACC110.currow,"BXBXCD");
		var wdth=ds_FACC110.GetColumn(ds_FACC110.currow,"BXWDTH");
		var lnth=ds_FACC110.GetColumn(ds_FACC110.currow,"BXLNTH");
		var hiht=ds_FACC110.GetColumn(ds_FACC110.currow,"BXHIHT");

		if(length(bxcd)!=0){
			if(length(wdth)==0 || wdth<1){
				alert(setPopMessage("6007"));//[알림]가로길이를 입력하세요.
				setMessage("6007");
				result=false;
			}else if(length(lnth)==0 || lnth<1){
				alert(setPopMessage("6008"));//[알림]세로길이를 입력하세요.
				setMessage("6008");
				result=false;
			}else if(length(hiht)==0 || hiht<1){
				alert(setPopMessage("6009"));//[알림]높이를 입력하세요.
				setMessage("6009");
				result=false;
			}
		}		
	return result;		
	}
}

function ed_BXBXCD_OnKeyDown(obj,nChar,bShift,bCtrl, bAlt,LLParam,HLParam)
{
	if(nChar == 13){
		fn_Search();
	}
}

function ed_BXBXCD_OnCharChanged(obj,strPreText,strPostText)
{
	ds_FACC110.ClearData();
}
