function getFGPE271Q(){

	if(!validationChk()){
		return;
	}	
	var procs2 = "";
	procs2 = ed_PROCS2.Text;
	if(procs2==""){
		procs2="9999999999";
	}
	getFGP180PF();
	var qry1="";
	var qry2="";
	var qry3="";
	var qry4="";
	for(i=0; i<ds_FGP180PF.rowcount; i++){
		MainGrid0.SetCellProp("Head", 11+i, "text",
		 substr(lpad(ds_FGP180PF.GetColumn(i,"WRKST"),0,4),0,2)+":"+substr(lpad(ds_FGP180PF.GetColumn(i,"WRKST"),0,4),2,2)
		 +"~"+substr(lpad(ds_FGP180PF.GetColumn(i,"WRKED"),0,4),0,2)+":"+substr(lpad(ds_FGP180PF.GetColumn(i,"WRKED"),0,4),2,2));			
		//eval("wrkst"+(i+1)+"=ds_FGP180PF.GetColumn("+i+","+"'WRKST'"+")"+';');	
		if(cmb_DAYGU.Value=="D"){
			if(i==0){
				//A.PRCT6 -> PRSTM 변경(2018.07.16)
				//qry1+="CASE WHEN SUBSTR(REPEAT('0',6-length(trim(char(A.PRCT6)))) || A.PRCT6,1,4)< "+ lpad(ds_FGP180PF.GetColumn(i,"WRKED"),0,4)+ " THEN '" + lpad(ds_FGP180PF.GetColumn(i,"WRKST"),0,4)+"'";
				qry1+="CASE WHEN SUBSTR(REPEAT('0',6-length(trim(char(A.PRSTM )))) || A.PRSTM,1,4)< "+ lpad(ds_FGP180PF.GetColumn(i,"WRKED"),0,4)+ " THEN '" + lpad(ds_FGP180PF.GetColumn(i,"WRKST"),0,4)+"'";
				qry2+="CASE WHEN A.ARRDT < "+  lpad(ds_FGP180PF.GetColumn(i,"WRKED"),0,4)+" THEN SUM(A.PRTQY) ELSE '' END PRYQY"+(i+1);
				qry3+="SUM(A.PRYQY"+(i+1)+") AS PRYQY"+(i+1);
				qry4+="E.PRYQY"+(i+1);
			}
			else if(i!=0 && i!=(ds_FGP180PF.rowcount-1)){
				//qry1+=" WHEN SUBSTR(REPEAT('0',6-length(trim(char(A.PRCT6)))) || A.PRCT6,1,4)>= "+ lpad(ds_FGP180PF.GetColumn(i,"WRKST"),0,4)+ " AND SUBSTR(REPEAT('0',6-length(trim(char(A.PRCT6)))) || A.PRCT6,1,4) < "+ lpad(ds_FGP180PF.GetColumn(i,"WRKED"),0,4)+" THEN '"+lpad(ds_FGP180PF.GetColumn(i,"WRKST"),0,4)+"'";
				qry1+=" WHEN SUBSTR(REPEAT('0',6-length(trim(char(A.PRSTM)))) || A.PRSTM,1,4)>= "+ lpad(ds_FGP180PF.GetColumn(i,"WRKST"),0,4)+ " AND SUBSTR(REPEAT('0',6-length(trim(char(A.PRSTM)))) || A.PRSTM,1,4) < "+ lpad(ds_FGP180PF.GetColumn(i,"WRKED"),0,4)+" THEN '"+lpad(ds_FGP180PF.GetColumn(i,"WRKST"),0,4)+"'";
				qry2+=", CASE WHEN A.ARRDT >= "+  lpad(ds_FGP180PF.GetColumn(i,"WRKST"),0,4)+" AND A.ARRDT < "+ lpad(ds_FGP180PF.GetColumn(i,"WRKED"),0,4)+ " THEN SUM(A.PRTQY) ELSE '' END PRYQY"+(i+1);
				qry3+=", SUM(A.PRYQY"+(i+1)+") AS PRYQY"+(i+1);
				qry4+=", E.PRYQY"+(i+1);
			}
			else if(i!=0 && i==(ds_FGP180PF.rowcount-1)){
				//qry1+=" WHEN SUBSTR(REPEAT('0',6-length(trim(char(A.PRCT6)))) || A.PRCT6,1,4) >= "+ lpad(ds_FGP180PF.GetColumn(i,"WRKST"),0,4)+ " THEN '" + lpad(ds_FGP180PF.GetColumn(i,"WRKST"),0,4)+"' END ARRDT";
				qry1+=" WHEN SUBSTR(REPEAT('0',6-length(trim(char(A.PRSTM)))) || A.PRSTM,1,4) >= "+ lpad(ds_FGP180PF.GetColumn(i,"WRKST"),0,4)+ " THEN '" + lpad(ds_FGP180PF.GetColumn(i,"WRKST"),0,4)+"' END ARRDT";
				qry2+=", CASE WHEN A.ARRDT >= "+  lpad(ds_FGP180PF.GetColumn(i,"WRKST"),0,4)+" THEN SUM(A.PRTQY) ELSE '' END PRYQY"+(i+1);
				qry3+=", SUM(A.PRYQY"+(i+1)+") AS PRYQY"+(i+1);
				qry4+=", E.PRYQY"+(i+1);
			}
		}else{
			if(i==0){
				//qry1+="CASE WHEN SUBSTR(REPEAT('0',6-length(trim(char(A.PRCT6)))) || A.PRCT6,1,4)>= "+ lpad(ds_FGP180PF.GetColumn(i,"WRKST"),0,4)+ " AND SUBSTR(REPEAT('0',6-length(trim(char(A.PRCT6)))) || A.PRCT6,1,4) < "+ lpad(ds_FGP180PF.GetColumn(i,"WRKED"),0,4)+" THEN '"+lpad(ds_FGP180PF.GetColumn(i,"WRKST"),0,4)+"'";
				qry1+="CASE WHEN SUBSTR(REPEAT('0',6-length(trim(char(A.PRSTM)))) || A.PRSTM,1,4)>= "+ lpad(ds_FGP180PF.GetColumn(i,"WRKST"),0,4)+ " AND SUBSTR(REPEAT('0',6-length(trim(char(A.PRSTM)))) || A.PRSTM,1,4) < "+ lpad(ds_FGP180PF.GetColumn(i,"WRKED"),0,4)+" THEN '"+lpad(ds_FGP180PF.GetColumn(i,"WRKST"),0,4)+"'";
				qry2+="CASE WHEN A.ARRDT >= "+  lpad(ds_FGP180PF.GetColumn(i,"WRKST"),0,4)+" AND A.ARRDT < "+ lpad(ds_FGP180PF.GetColumn(i,"WRKED"),0,4)+ " THEN SUM(A.PRTQY) ELSE '' END PRYQY"+(i+1);
				qry3+="SUM(A.PRYQY"+(i+1)+") AS PRYQY"+(i+1);
				qry4+="E.PRYQY"+(i+1);
			}
			else if(i!=0 && i!=(ds_FGP180PF.rowcount-1)){
				//qry1+=" WHEN SUBSTR(REPEAT('0',6-length(trim(char(A.PRCT6)))) || A.PRCT6,1,4)>= "+ lpad(ds_FGP180PF.GetColumn(i,"WRKST"),0,4)+ " AND SUBSTR(REPEAT('0',6-length(trim(char(A.PRCT6)))) || A.PRCT6,1,4) < "+ lpad(ds_FGP180PF.GetColumn(i,"WRKED"),0,4)+" THEN '"+lpad(ds_FGP180PF.GetColumn(i,"WRKST"),0,4)+"'";
				qry1+=" WHEN SUBSTR(REPEAT('0',6-length(trim(char(A.PRSTM)))) || A.PRSTM,1,4)>= "+ lpad(ds_FGP180PF.GetColumn(i,"WRKST"),0,4)+ " AND SUBSTR(REPEAT('0',6-length(trim(char(A.PRSTM)))) || A.PRSTM,1,4) < "+ lpad(ds_FGP180PF.GetColumn(i,"WRKED"),0,4)+" THEN '"+lpad(ds_FGP180PF.GetColumn(i,"WRKST"),0,4)+"'";
				qry2+=", CASE WHEN A.ARRDT >= "+  lpad(ds_FGP180PF.GetColumn(i,"WRKST"),0,4)+" AND A.ARRDT < "+ lpad(ds_FGP180PF.GetColumn(i,"WRKED"),0,4)+ " THEN SUM(A.PRTQY) ELSE '' END PRYQY"+(i+1);
				qry3+=", SUM(A.PRYQY"+(i+1)+") AS PRYQY"+(i+1);
				qry4+=", E.PRYQY"+(i+1);
			}
			else if(i!=0 && i==(ds_FGP180PF.rowcount-1)){
				//qry1+=" WHEN SUBSTR(REPEAT('0',6-length(trim(char(A.PRCT6)))) || A.PRCT6,1,4) >= "+ lpad(ds_FGP180PF.GetColumn(i,"WRKST"),0,4)+ " AND SUBSTR(REPEAT('0',6-length(trim(char(A.PRCT6)))) || A.PRCT6,1,4) < "+ lpad(ds_FGP180PF.GetColumn(0,"WRKST"),0,4)+" THEN '"+lpad(ds_FGP180PF.GetColumn(i,"WRKST"),0,4)+"' END ARRDT";
				qry1+=" WHEN SUBSTR(REPEAT('0',6-length(trim(char(A.PRSTM)))) || A.PRSTM,1,4) >= "+ lpad(ds_FGP180PF.GetColumn(i,"WRKST"),0,4)+ " AND SUBSTR(REPEAT('0',6-length(trim(char(A.PRSTM)))) || A.PRSTM,1,4) < "+ lpad(ds_FGP180PF.GetColumn(0,"WRKST"),0,4)+" THEN '"+lpad(ds_FGP180PF.GetColumn(i,"WRKST"),0,4)+"' END ARRDT";
				qry2+=", CASE WHEN A.ARRDT >= "+  lpad(ds_FGP180PF.GetColumn(i,"WRKST"),0,4)+" AND A.ARRDT < "+ lpad(ds_FGP180PF.GetColumn(0,"WRKST"),0,4)+ " THEN SUM(A.PRTQY) ELSE '' END PRYQY"+(i+1);
				qry3+=", SUM(A.PRYQY"+(i+1)+") AS PRYQY"+(i+1);
				qry4+=", E.PRYQY"+(i+1);
