	var sArgument   = "";
		sArgument += "@LIB2=" + quote(Lib2);
		sArgument += "@COMPS=" + quote(ComboBoxPlant.Value); //사업부
		sArgument += "@ORDAT=" + quote(cmb_ORDAT.Value); //작업일자
		sArgument += "@DAYGU=" + quote(cmb_DAYGU.Value); //주야구분
		sArgument += "@PROCS1=" + quote(ed_PROCS1.Text); //작업공정FR
		sArgument += "@PROCS2=" + quote(procs2); //작업공정TO
		sArgument += "@QRY1=" + quote(qry1); //작업공정TO
		sArgument += "@QRY2=" + quote(qry2); //작업공정TO
		sArgument += "@QRY3=" + quote(qry3); //작업공정TO
		sArgument += "@QRY4=" + quote(qry4); //작업공정TO

	//trace(sArgument);

	var str = dialog("Common::PGMLOAD_FAC.xml","sKind=" + sKind + " " + "sMethodName=" + sMethodName + " " + "sOutDataSet=" + sOutDataSet + " " + "sArgument=" + sArgument,272,64,"TitleBar=false");
	if (str == 'T'){  //정상조회
		var arrProcs = "('";
		for(i=0; i< ds_FGPE271Q.rowcount; i++){
			if(i == ds_FGPE271Q.rowcount-1){
				if(ds_FGPE271Q.GetColumn(i,"PROCS") != ds_FGPE271Q.GetColumn(i+1,"PROCS")){
					arrProcs = arrProcs+ds_FGPE271Q.GetColumn(i,"PROCS") + "')";
				}
			}else{
				if(ds_FGPE271Q.GetColumn(i,"PROCS") != ds_FGPE271Q.GetColumn(i+1,"PROCS")){
					arrProcs = arrProcs+ds_FGPE271Q.GetColumn(i,"PROCS") + "','";
				}
			}
		}
		getFGP120PF_NOTWORK(arrProcs);
		getFGP140PF_MAN(arrProcs);
		getFGP150PF_EVENT(arrProcs);	
		alert(setPopMessage("1004"));
		setMessage("1004");
	}else if (str == 'B'){   //data값이 없을때
		alert(setPopMessage("1003"));
		setMessage("1003");
	}else if (str == 'E'){   //error발생시
		alert(setPopMessage("1002"));
		setMessage("1002");	
	}
//trace(ds_FGPE271Q.SaveXML());
}

/********************************************************************** 
 * 내용설명 : validation 체크
 **********************************************************************/ 

function validationChk(){
	var result = true;
	var ordat = cmb_ORDAT.Value;
	var daygu = cmb_DAYGU.Value;
		cmb_ORDAT.BKColor="white";
		cmb_DAYGU.BKColor="white";

	if (ordat == ""){
		cmb_ORDAT.BKColor="paleturquoise";
		//alert("[알림]작업일자을 확인바랍니다.");
		alert(setPopMessage("4061"));//[알림]작업일자을 확인바랍니다.
		setMessage("4061");
		result = false;
	}else if (length(daygu) == 0){
		cmb_DAYGU.BKColor="paleturquoise";
		//alert("[알림]주야구분을 입력하세요.");
		alert(setPopMessage("2027"));//[알림] 주야구분을 선택하세요.
		setMessage("2027");
		result = false;
	}
	return result;
}

/********************************************************************** 
 * 내용설명 : 상위메뉴 변경시 초기화
 **********************************************************************/ 
function common_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	ds_FGPE271Q.Clear();
	ds_FGP180PF.ClearData();
	ds_FGP120PF.ClearData();
	ds_FGP140PF.ClearData();
	ds_FGP150PF.ClearData();
	//trace(ds_FGPE271Q.SaveXML());
}

/********************************************************************** 
 * 내용설명 : 엑셀파일로 저장
 **********************************************************************/ 
function setExcel()
{
	var fName   = "전광판일가동일보현황"+cmb_ORDAT.Value;
	var arrSheets  =  Array();  

	arrSheets[0]  = "MainGrid3|작업인원현황";
	arrSheets[1]  = "MainGrid4|이상발생현황";
	arrSheets[2]  = "MainGrid2|비가동현황";
	arrSheets[3]  = "MainGrid0|전광판일가동일보현황";

	createExcelSheetS2(FileDialog0, fName ,arrSheets);
}

