function menu_OnFocus(obj)
{
	gFcOnTopButtonColor("1"); //상단 버튼 활성화 여부 (기본:0,조회:1,변경:2)
}

function cmb_ORDAT_OnKillFocus(obj)
{
	if(length(trim(obj.Value)) < 1) return;

	http.usewaitcursor = false;
	http.Sync 	= true;
	var sKind		= "getSC003PR";		
	var sMethodName = "service::pscomlib/selectSC003PR.do"; 
	var sInDataSet  = "";
	var sOutDataSet = "ds_SC003PR=ds_SC003PR";
	var sArgument   = "";
		sArgument += " LIB1=" + quote(Lib1);
		sArgument += " PRMNO=" + quote("ABS"); //사업부
		sArgument += " YRMON=" + quote(substr(obj.Value,0,6)); //계획년월
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	if (ds_SC003PR.GetRowCount() < 1){
		//alert("[알림]일자를 확인하세요.");
		alert(setPopMessage("6029"));//[알림]일자를 확인하세요.
		setMessage("6029");	
		cmb_ORDAT.SetFocus();
		return;
	}
	http.usewaitcursor = true;
}

function getFGP180PF(){
	var sKind		= "getFGP180PF";		
	var sMethodName = "service::psfaclib/selectFGP180PF.do"; 
	var sInDataSet  = "";
	var sOutDataSet = "ds_FGP180PF=ds_FGP180PF";
	var sArgument   = "";
		sArgument += " LIB2=" + quote(Lib2);
		sArgument += " COMPS=" + quote(ComboBoxPlant.Value); //사업부
		sArgument += " DAYGU=" + quote(cmb_DAYGU.Value); //주야구분
	http.Sync=true;
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	http.Sync=false;
}
