		sArgument += " COMPS=" + quote(ComboBoxPlant.Value); //사업부
		sArgument += " DAYGU=" + quote(cmb_DAYGU.Value); //주야구분
		sArgument += " ORDAT=" + quote(cmb_ORDAT.Value); //주야구분
		sArgument += " PROCS=" + quote(procs); //작업공정
	http.Sync=true;
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	http.Sync=false;
}

function ed_PROCS2_OnKeyDown(obj,nChar,bShift,bCtrl, bAlt,LLParam,HLParam)
{
	if(nChar == 13){
		fn_Search();
	}	
}
