var strSeparator = "	";
var Lib1 = G_ds_user.GetColumn(0,"LIB1"); // cigma library(pereslib,psreslib)
var Lib2 = G_ds_user.GetColumn(0,"LIB2"); // 회사 library(psedblib,pscdblib)
var Lib3 = G_ds_user.GetColumn(0,"LIB3"); // 사업부 library(psedblib,pscdblib,pshdblib,pssdblib)
var company = G_ds_user.GetColumn(0,"COMPANY");
var comps = G_ds_user.GetColumn(0,"PLANT_CD");

function on_LoadCompleted(obj){
	getTitle();	
	getDesign();
	gFcOnTopButtonColor("2.2"); //조회, 추가, 저장
	getComCode(); //공장구분 선택
}

function getComCode(){
	var sKind		= "getComCode";						
	var sMethodName = "service::login/selectComCode.do"; 
	var sInDataSet  = "";
	var sOutDataSet = "ds_ComCode_PLNT=ds_ComCode_PLNT";
	var sArgument   = "";
		sArgument   += " CLASS='PLNT'";
		sArgument   += " CODE1=" + quote(company);		
	http.Sync=true;
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	http.Sync=false;
	ComboBoxPlant.Value=comps;
	ComboBoxPlant.Enable = false; 
}

function getDesign(){
	//MainGrid.ToolTipText = setPopMessage("2053");
	MainGrid.BkColor = G_ds_design.GetColumn(0,"BkColor");
	MainGrid.BkColor2 = G_ds_design.GetColumn(0,"BkColor2");
	MainGrid.BkSelColor = G_ds_design.GetColumn(0,"BkSelColor");
	MainGrid.Face3dColor = G_ds_design.GetColumn(0,"Face3dColor");
	MainGrid.BoldHead = G_ds_design.GetColumn(0,"BoldHead");
	MainGrid.HeadHeight = G_ds_design.GetColumn(0,"HeadHeight");
	//MainGrid.Width = G_ds_design.GetColumn(0,"Width");

	searchBar.Width = G_ds_design.GetColumn(0,"Width");
	searchBar.Top = G_ds_design.GetColumn(0,"SBTop");	
}

function getFGPZ110(){

	if(!validationChk()){
		return;
	}
	var sKind		= "getFGPZ110";						
	var sMethodName = "service::psfaclib/selectFGPZ110.do";     		// 서버에서 호출 될 Method 명
	var sInDataSet  = "";                             				// 자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet = "ds_FGPZ110=ds_FGPZ110";    				// 조회 후 돌려 받을 DataSet 명칭
	var sArgument   = "";
		sArgument += "@LIB2=" + quote(Lib2);
		sArgument += "@COMPS=" + quote(ComboBoxPlant.Value); //사업부
		sArgument += "@CLSGU=" + quote(cmb_CLSGU.Value); //발생코드
	var str = dialog("Common::PGMLOAD_FAC.xml","sKind=" + sKind + " " + "sMethodName=" + sMethodName + " " + "sOutDataSet=" + sOutDataSet + " " + "sArgument=" + sArgument,272,64,"TitleBar=false");
	if (str == 'T'){  //정상조회
		alert(setPopMessage("1004"));
		setMessage("1004");
		getFGPZ030_CLSLG();
		getFGPZ030_CLSMD();
		getFGPZ030_CLSSM();		
	}else if (str == 'B'){   //data값이 없을때
		alert(setPopMessage("1003"));
		setMessage("1003");
		getFGPZ030_CLSLG();
		getFGPZ030_CLSMD();
		getFGPZ030_CLSSM();		
	}else if (str == 'E'){   //error발생시
		alert(setPopMessage("1002"));
		setMessage("1002");	
	}

}

/********************************************************************** 
 * 내용설명 : insert, update, delete
 **********************************************************************/  
function setFGPZ110(){

	if(ds_FGPZ110.GetRowType(ds_FGPZ110.currow)=="update" ||ds_FGPZ110.GetRowType(ds_FGPZ110.currow)=="insert" ){
		if(!ds_FGPZ110_CanRowPosChange()){
			return;
		}
	} 

	var sKind		= "setFGPZ110";						
	var sMethodName = "service::psfaclib/saveFGPZ110.do";    // 서버에서 호출 될 Method 명
	var sInDataSet  = "ds_FGPZ110=ds_FGPZ110:U";               //자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet = "ds_FGPZ110_CHECK=ds_FGPZ110_CHECK";  	   // 조회 후 돌려 받을 DataSet 명칭
	var sArgument   = "";
		sArgument += " LIB2=" + quote(Lib2);
	http.Sync=true;
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "callback");
	http.Sync=false;

}
/********************************************************************** 
 * 내용설명 : Transaction 리턴후 실행 함수
 * 주요변수 : 
 * 			- sMethodName	Transaction 고유 ID
 * 			- ErrorCode 	에러코드
 * 			- ErrorMSG 		에러 메세지
 **********************************************************************/ 
function callback(svcId, rtnCd, rtnMsg){
	if (ds_FGPZ110_CHECK.GetRowCount() > 0){
		for(var i=0;i<ds_FGPZ110_CHECK.GetRowCount();i++){
			alert("대분류 : " +ds_FGPZ110_CHECK.GetColumn(i,"CLSLG") + ", 중분류 : " +ds_FGPZ110_CHECK.GetColumn(i,"CLSMD") + ", 소분류 : " +ds_FGPZ110_CHECK.GetColumn(i,"CLSSM") + " = " + ds_FGPZ110_CHECK.GetColumn(i,"RTMSG"));
		}
		getFGPZ110();  //error항목이 존재시에는 재조회
		return;
	}// tran 실패
	if(rtnCd<0){
		alert(setPopMessage("1002"));
		setMessage("1002");

	}else{
		alert(setPopMessage("1001"));
		setMessage("1001");	
	}
}

/********************************************************************** 
 * 내용설명 : validation 체크
 **********************************************************************/ 

function validationChk(){
	var result = true;
	var clsgu = cmb_CLSGU.Text;
		cmb_CLSGU.BKColor="white";

	if (clsgu == "")
	{
		cmb_CLSGU.BKColor="paleturquoise";
		alert(setPopMessage("6010"));//[알림]발생코드를 입력하세요.
		setMessage("6010");
		result = false;
	}
	return result;
}

/********************************************************************** 
 * 내용설명 : 상위메뉴 변경시 초기화
 **********************************************************************/ 
function common_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	ds_FGPZ110.ClearData();
}

/********************************************************************** 
 * 내용설명 : 엑셀파일로 저장
 **********************************************************************/ 
function setExcel()
{
	var fName   = "전광판이상불량코드마스타";
	var arrSheets  =  Array();  

	arrSheets[0]  = "MainGrid|전광판이상불량코드마스타";
	createExcelSheetS2(FileDialog0, fName ,arrSheets);

}

/********************************************************************** 
 * 내용설명 : Sort
 **********************************************************************/ 
function MainGrid_OnHeadClick(obj,nCell,nX,nY,nPivotIndex)
{
	if(nCell != -1) lib_grid_SetGridSort(obj,nCell);
}

