}

function fn_Search()
{
	getFGPZ110();
}

function fn_Input()
{
	if(checkInsertCount("ds_FGPZ110")== false) return;
	if(ds_FGPZ110.rowcount < 1) fn_Search();  
	ds_FGPZ110.AddRow();
	ds_FGPZ110.SetColumn(ds_FGPZ110.currow,"COMPS",ComboBoxPlant.Value);
	ds_FGPZ110.SetColumn(ds_FGPZ110.currow,"CLSGU",cmb_CLSGU.Value);
}

function fn_Delete()
{
	if(ds_FGPZ110.rowcount < 1) return;			//삭제가 없으면 건너뛰기		
	ds_FGPZ110.DeleteRow(ds_FGPZ110.currow);	//삭제		
	alert(setPopMessage("1011"));				//[알림] 화면에서만 제거되었습니다. 저장하시면 삭제됩니다.		
	setMessage("1011");
}

function fn_Save()
{
	setFGPZ110();
}

function fn_print()
{
	alert(setPopMessage("9999"));
	setMessage("9999");
}

function fn_Excel()
{
	setExcel();
}

function fn_Help()
{
