}

function menu_OnFocus(obj)
{
	gFcOnTopButtonColor("2.2"); //상단 버튼 활성화 여부 (기본:0,조회:1,변경:2)
}

function getFGPZ030_CLSLG(){

	var clslg="";
	if(cmb_CLSGU.Value=="S"){
		clslg="SLG";
	}
	else{
		clslg="BLG";
	}
	var sKind		= "getFGPZ030_CLSLG";						
	var sMethodName = "service::psfaclib/selectFGP030_SELGP.do";     		// 서버에서 호출 될 Method 명
	var sInDataSet  = "";                             				// 자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet = "ds_CLSLG=ds_SELGP";    				// 조회 후 돌려 받을 DataSet 명칭
	var sArgument   = "";
		sArgument += " LIB2=" + quote(Lib2);
		sArgument += " COMPS=" + quote(ComboBoxPlant.Value); //사업부
		sArgument += " CLSGU=" + quote(cmb_CLSGU.Value); //발생코드
		sArgument += " GGPCD=" + quote(clslg); //대그룹코드
	http.Sync=true;
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	http.Sync=false;
}
