function ds_FGPZ110_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	if (strColumnID == "CLSLG"){
		ds_FGPZ110.SetColumn(nRow,"CLGNM",ds_CLSLG.GetColumn(ds_CLSLG.FindRow("T1",ds_FGPZ110.GetColumn(nRow,"CLSLG")),"T2"));
	}
	if (strColumnID == "CLSMD"){
		ds_FGPZ110.SetColumn(nRow,"CMDNM",ds_CLSMD.GetColumn(ds_CLSMD.FindRow("T1",ds_FGPZ110.GetColumn(nRow,"CLSMD")),"T2"));
	}
	if (strColumnID == "CLSSM"){
		ds_FGPZ110.SetColumn(nRow,"CSMNM",ds_CLSSM.GetColumn(ds_CLSSM.FindRow("T1",ds_FGPZ110.GetColumn(nRow,"CLSSM")),"T2"));
	}
}

function ds_FGPZ110_CanRowPosChange(obj,nOldRow,nRow)
{
	var result=true;
	if(ds_FGPZ110.GetRowType(ds_FGPZ110.currow)=="update" ||ds_FGPZ110.GetRowType(ds_FGPZ110.currow)=="insert" ){
		var clslg=ds_FGPZ110.GetColumn(ds_FGPZ110.currow,"CLSLG");
		var clsmd=ds_FGPZ110.GetColumn(ds_FGPZ110.currow,"CLSMD");
		var clssm=ds_FGPZ110.GetColumn(ds_FGPZ110.currow,"CLSSM");

		if(length(clslg)!=0 || length(clsmd)!=0 || length(clssm)!=0){
			if(length(clslg)==0){
				//alert("[알림]대분류코드를 입력하세요.");
				alert(setPopMessage("6012"));//[알림]대분류코드를 입력하세요.
				setMessage("6012");
				result=false;
			}else if(length(clsmd)==0){
				//alert("[알림]중분류코드를 입력하세요.");
				alert(setPopMessage("6013"));//[알림]중분류코드를 입력하세요.
				setMessage("6013");
				result=false;
			}else if(length(clssm)==0){
				//alert("[알림]소분류코드를 입력하세요.");
				alert(setPopMessage("6014"));//[알림]소분류코드를 입력하세요.
				setMessage("6014");
				result=false;
			}
		}
		return result;		
	}	
}

function cmb_CLSGU_OnKeyDown(obj,nChar,bShift,bCtrl,bAlt,LLParam,HLParam)
{
	if(nChar == 13){
		fn_Search();
	}
}
